  --sidebar-border: 25 20% 85%;

  --sidebar-primary: 25 45% 35%;

  --sidebar-primary-foreground: 45 25% 95%;

  --sidebar-accent: 35 25% 90%;

  --sidebar-accent-foreground: 25 45% 35%;

  --sidebar-ring: 25 45% 35%;

