import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertRecipeSchema, type InsertRecipe, type Recipe, type Product, recipeIngredientSchema } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { ChefHat, Plus, Trash2, X } from "lucide-react";
import { z } from "zod";

interface RecipeFormProps {
  onSubmit: (recipe: InsertRecipe) => void;
  products: Product[];
  editRecipe?: Recipe;
  onCancel?: () => void;
}

const ingredientFormSchema = z.object({
  productId: z.string().min(1, "Seleziona un ingrediente"),
  quantity: z.number().min(0.01, "Inserisci una quantità valida"),
});

export default function RecipeForm({ onSubmit, products, editRecipe, onCancel }: RecipeFormProps) {
  const [isEditing, setIsEditing] = useState(!!editRecipe);
  const [ingredients, setIngredients] = useState(editRecipe?.ingredients || []);
  const [selectedProductId, setSelectedProductId] = useState("");
  const [quantity, setQuantity] = useState("");
  const [ingredientWeightAdjustment, setIngredientWeightAdjustment] = useState("0");

  const form = useForm<{ name: string; weightAdjustment: number }>({
    resolver: zodResolver(z.object({ 
      name: z.string().min(1, "Nome ricetta richiesto"),
      weightAdjustment: z.number().min(-100).max(1000).default(0)
    })),
    defaultValues: {
      name: editRecipe?.name || "",
      weightAdjustment: editRecipe?.weightAdjustment || 0,
    },
  });

  // Update form and state when editRecipe prop changes
  useEffect(() => {
    if (editRecipe) {
      setIsEditing(true);
      setIngredients(editRecipe.ingredients || []);
      form.reset({
        name: editRecipe.name,
        weightAdjustment: editRecipe.weightAdjustment || 0,
      });
    } else {
      setIsEditing(false);
      setIngredients([]);
      form.reset({
        name: "",
        weightAdjustment: 0,
      });
    }
  }, [editRecipe, form]);

  const selectedProduct = products.find(p => p.id === selectedProductId);
  const totalCost = ingredients.reduce((sum, ing) => sum + ing.cost, 0);

  const addIngredient = () => {
    if (!selectedProductId || !quantity) return;

    const product = products.find(p => p.id === selectedProductId);
    if (!product) return;

    const parsedQuantity = parseFloat(quantity);
    if (parsedQuantity <= 0) return;

    const cost = parsedQuantity * product.pricePerUnit;
    const parsedWeightAdjustment = parseFloat(ingredientWeightAdjustment) || 0;
    const ingredient = {
      productId: selectedProductId,
      quantity: parsedQuantity,
      cost,
      weightAdjustment: parsedWeightAdjustment,
    };

    console.log("Adding ingredient:", ingredient);
    setIngredients([...ingredients, ingredient]);
    setSelectedProductId("");
    setQuantity("");
    setIngredientWeightAdjustment("0");
  };

  const removeIngredient = (index: number) => {
    console.log("Removing ingredient at index:", index);
    setIngredients(ingredients.filter((_, i) => i !== index));
  };

  const handleSubmit = (data: { name: string; weightAdjustment: number }) => {
    if (ingredients.length === 0) return;

    const recipe: InsertRecipe = {
      name: data.name,
      ingredients,
      weightAdjustment: data.weightAdjustment,
      totalCost,
    };

    console.log("Recipe form submitted:", recipe);
    onSubmit(recipe);

    if (!isEditing) {
      form.reset();
      setIngredients([]);
    }
  };

  const handleCancel = () => {
    console.log("Recipe form cancelled");
    form.reset();
    setIngredients([]);
    onCancel?.();
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <ChefHat className="h-5 w-5 text-primary" />
          {isEditing ? "Modifica Ricetta" : "Crea Ricetta"}
        </CardTitle>
      </CardHeader>
      <CardContent>
        <Form {...form}>
          <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-6">
            <FormField
              control={form.control}
              name="name"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Nome Ricetta</FormLabel>
                  <FormControl>
                    <Input
                      {...field}
                      placeholder="Es. Pasta Frolla"
                      className="bg-yellow-100 dark:bg-yellow-900/30"
                      data-testid="input-recipe-name"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="weightAdjustment"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Peso +/- (%)</FormLabel>
                  <FormControl>
                    <Input
                      {...field}
                      type="number"
                      step="0.1"
                      min="-100"
                      max="1000"
                      placeholder="0"
                      onChange={(e) => field.onChange(parseFloat(e.target.value) || 0)}
                      className="bg-yellow-100 dark:bg-yellow-900/30"
                      data-testid="input-recipe-weight-adjustment"
                    />
                  </FormControl>
                  <div className="text-xs text-muted-foreground mt-1">
                    Aumento/diminuzione peso dopo lavorazione (es. +70% per pasta cotta)
                  </div>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="border-t pt-4">
              <h3 className="font-semibold text-foreground mb-4">Ingredienti</h3>

              <div className="space-y-4">
                <div className="grid grid-cols-3 gap-2">
                  <Select value={selectedProductId} onValueChange={setSelectedProductId}>
                    <SelectTrigger data-testid="select-ingredient">
                      <SelectValue placeholder="Seleziona un ingrediente" />
                    </SelectTrigger>
                    <SelectContent>
                      {products.map((product) => (
                        <SelectItem key={product.id} value={product.id}>
                          {product.name} ({product.unit})
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>

                  <Input
                    type="number"
                    step="0.01"
                    min="0"
                    placeholder="Quantità"
                    value={quantity}
                    onChange={(e) => setQuantity(e.target.value)}
                    className="bg-yellow-100 dark:bg-yellow-900/30"
                    data-testid="input-ingredient-quantity"
                  />

                  <Input
                    type="number"
                    step="0.1"
                    min="-100"
                    max="1000"
                    placeholder="Peso +/- %"
                    value={ingredientWeightAdjustment}
                    onChange={(e) => setIngredientWeightAdjustment(e.target.value)}
                    className="bg-yellow-100 dark:bg-yellow-900/30"
                    data-testid="input-ingredient-weight-adjustment"
                  />
                </div>

                {selectedProduct && (
                  <div className="grid grid-cols-2 gap-2">
                    <Input
                      readOnly
                      value={selectedProduct.unit}
                      className="bg-muted text-muted-foreground"
                    />
                    <Input
                      readOnly
                      value={`€${selectedProduct.pricePerUnit.toFixed(2)}`}
                      className="bg-muted text-muted-foreground"
                    />
                  </div>
                )}

                <Button
                  type="button"
                  onClick={addIngredient}
                  disabled={!selectedProductId || !quantity}
                  className="w-full"
                  data-testid="button-add-ingredient"
                >
                  <Plus className="h-4 w-4 mr-2" />
                  Aggiungi ingrediente
                </Button>
              </div>

              {ingredients.length > 0 && (
                <div className="mt-4 space-y-2">
                  {ingredients.map((ingredient, index) => {
                    const product = products.find(p => p.id === ingredient.productId);
                    return (
                      <Card key={index} className="p-3">
                        <div className="flex items-center justify-between">
                          <div className="flex items-center gap-2">
                            <span className="font-medium">{product?.name}</span>
                            <Badge variant="secondary" className="text-xs">
                              {ingredient.quantity} {product?.unit}
                            </Badge>
                            {ingredient.weightAdjustment !== 0 && (
                              <Badge variant="outline" className="text-xs">
                                {ingredient.weightAdjustment > 0 ? '+' : ''}{ingredient.weightAdjustment}%
                              </Badge>
                            )}
                            <span className="text-sm text-muted-foreground">
                              €{ingredient.cost.toFixed(2)}
                            </span>
                          </div>
                          <Button
                            type="button"
                            variant="destructive"
                            size="icon"
                            onClick={() => removeIngredient(index)}
                            data-testid={`button-remove-ingredient-${index}`}
                          >
                            <X className="h-4 w-4" />
                          </Button>
                        </div>
                      </Card>
                    );
                  })}
                </div>
              )}

              <Card className="p-3 mt-4 bg-muted">
                <div className="flex justify-between items-center">
                  <span className="font-semibold">Costo Totale Ricetta:</span>
                  <span className="font-bold font-mono text-lg">
                    €{totalCost.toFixed(2)}
                  </span>
                </div>
              </Card>
            </div>


            <div className="flex gap-2 pt-4">
              <Button
                type="submit"
                disabled={ingredients.length === 0}
                className="flex-1"
                data-testid="button-submit-recipe"
              >
                {isEditing ? "Aggiorna Ricetta" : "Aggiungi Ricetta"}
              </Button>
              {isEditing && (
                <Button
                  type="button"
                  variant="secondary"
                  onClick={handleCancel}
                  className="flex-1"
                  data-testid="button-cancel-recipe"
                >
                  Annulla Modifiche
                </Button>
              )}
            </div>
          </form>
        </Form>
      </CardContent>
    </Card>
  );
}

client/src/components/RecipeList.tsx.content:
import { type Recipe, type Product } from "@shared/schema";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { ChefHat, Edit, Trash2, UtensilsCrossed } from "lucide-react";

interface RecipeListProps {
  recipes: Recipe[];
  products: Product[];
  onEdit?: (recipe: Recipe) => void;
  onDelete?: (recipeId: string) => void;
}

export default function RecipeList({ recipes, products, onEdit, onDelete }: RecipeListProps) {
  const handleEdit = (recipe: Recipe) => {
    console.log("Edit recipe:", recipe.id);
    onEdit?.(recipe);
  };

  const handleDelete = (recipeId: string) => {
    console.log("Delete recipe:", recipeId);
    onDelete?.(recipeId);
  };

  const getProductName = (productId: string) => {
    return products.find(p => p.id === productId)?.name || "Prodotto sconosciuto";
  };

  const getProductUnit = (productId: string) => {
    return products.find(p => p.id === productId)?.unit || "";
  };

  if (recipes.length === 0) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <UtensilsCrossed className="h-5 w-5 text-primary" />
            Ricette Salvate
          </CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-center text-muted-foreground italic">
            Nessuna ricetta ancora creata.
          </p>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <UtensilsCrossed className="h-5 w-5 text-primary" />
          Ricette Salvate
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        {recipes.map((recipe) => (
          <Card key={recipe.id} className="hover-elevate">
            <CardContent className="p-4">
              <div className="flex justify-between items-start gap-4">
                <div className="flex-1 space-y-3">
                  <div className="flex items-center gap-2">
                    <ChefHat className="h-4 w-4 text-muted-foreground" />
                    <span className="font-semibold text-foreground">{recipe.name}</span>
                  </div>

                  <div className="space-y-2">
                    <h4 className="text-sm font-medium text-muted-foreground">
                      Ingredienti:
                    </h4>
                    <div className="flex flex-wrap gap-2">
                      {recipe.ingredients.map((ingredient, index) => (
                        <Badge key={index} variant="outline" className="text-xs">
                          {getProductName(ingredient.productId)}: {ingredient.quantity} {getProductUnit(ingredient.productId)}
                        </Badge>
                      ))}
                    </div>
                  </div>

                  <div className="flex justify-between items-center">
                    <span className="text-sm text-muted-foreground">
                      Costo Totale:
                    </span>
                    <span className="font-bold font-mono text-destructive">
                      €{recipe.totalCost.toFixed(2)}
                    </span>
                  </div>
                </div>

                <div className="flex gap-2">
                  <Button
                    variant="outline"
                    size="icon"
                    onClick={() => handleEdit(recipe)}
                    data-testid={`button-edit-recipe-${recipe.id}`}
                  >
                    <Edit className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="destructive"
                    size="icon"
                    onClick={() => handleDelete(recipe.id)}
                    data-testid={`button-delete-recipe-${recipe.id}`}
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </CardContent>
    </Card>
  );
}

client/src/components/SalesSummary.tsx.content:
import { type Dish, type Product, type Waste, type PersonalMeal } from "@shared/schema";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { TrendingUp, DollarSign, AlertTriangle, Users, Info, Calendar } from "lucide-react";
import { useSales } from "@/hooks/useApi";
import { useMemo, useState, useEffect } from "react";

interface SalesSummaryProps {
  dishes: Dish[];
  products: Product[];
  waste: Waste[];
  personalMeals: PersonalMeal[];
  maxFoodCost?: number;
  onMaxFoodCostChange?: (value: number) => void;
  showSalesDetails?: boolean;
}

export default function SalesSummary({ 
  dishes, 
  products, 
  waste, 
  personalMeals, 
  maxFoodCost = 30,
  onMaxFoodCostChange,
  showSalesDetails = true 
}: SalesSummaryProps) {

  // Read selected year and month from localStorage (same as Dashboard)
  const [selectedYear, setSelectedYear] = useState(() => {
    const saved = localStorage.getItem('foodyflow-selected-year');
    return saved ? parseInt(saved) : new Date().getFullYear();
  });

  const [selectedMonth, setSelectedMonth] = useState(() => {
    const saved = localStorage.getItem('foodyflow-selected-month');
    return saved ? parseInt(saved) : new Date().getMonth() + 1;
  });

  // Update localStorage when values change
  useEffect(() => {
    localStorage.setItem('foodyflow-selected-year', selectedYear.toString());
  }, [selectedYear]);

  useEffect(() => {
    localStorage.setItem('foodyflow-selected-month', selectedMonth.toString());
  }, [selectedMonth]);

  // Fetch sales data
  const { data: allSalesData = [] } = useSales();

  // Filter sales data by selected month/year
  const filteredSalesData = useMemo(() => {
    return allSalesData.filter(sale => {
      const saleDate = new Date(sale.saleDate);
      return saleDate.getFullYear() === selectedYear && 
             saleDate.getMonth() + 1 === selectedMonth;
    });
  }, [allSalesData, selectedYear, selectedMonth]);

  // Calculate totals from filtered sales data
  const { totalCostOfSales, totalGrossSales, totalNetSales } = useMemo(() => {
    const totalCost = filteredSalesData.reduce((sum, sale) => sum + sale.totalCost, 0);
    const totalNetRevenue = filteredSalesData.reduce((sum, sale) => sum + sale.totalRevenue, 0);
    // Gross sales = net revenue + 10% (IVA)
    const totalGrossRevenue = totalNetRevenue * 1.10;
    return {
      totalCostOfSales: totalCost,
      totalGrossSales: totalGrossRevenue,
      totalNetSales: totalNetRevenue
    };
  }, [filteredSalesData]);

  // Filter waste and personal meals by selected month/year (same as sales)
  const filteredWaste = useMemo(() => {
    return waste.filter(w => {
      const wasteDate = new Date(w.date);
      return wasteDate.getFullYear() === selectedYear && 
             wasteDate.getMonth() + 1 === selectedMonth;
    });
  }, [waste, selectedYear, selectedMonth]);

  const filteredPersonalMeals = useMemo(() => {
    return personalMeals.filter(pm => {
      const mealDate = new Date(pm.date);
      return mealDate.getFullYear() === selectedYear && 
             mealDate.getMonth() + 1 === selectedMonth;
    });
  }, [personalMeals, selectedYear, selectedMonth]);

  const totalWasteCost = filteredWaste.reduce((sum, w) => sum + w.cost, 0);
  const totalPersonalMealsCost = filteredPersonalMeals.reduce((sum, pm) => sum + pm.cost, 0);

  // Calculate weighted food cost
  const weightedFoodCost = totalNetSales > 0 ? (totalCostOfSales / totalNetSales) * 100 : 0;
  const isOverThreshold = weightedFoodCost > maxFoodCost;

  const handleMaxFoodCostChange = (value: string) => {
    const numValue = Math.max(0, Math.min(100, parseFloat(value) || 0));
    console.log("Max food cost changed:", numValue);
    onMaxFoodCostChange?.(numValue);
  };

  // Month names in Italian
  const monthNames = [
    "Gennaio", "Febbraio", "Marzo", "Aprile", "Maggio", "Giugno",
    "Luglio", "Agosto", "Settembre", "Ottobre", "Novembre", "Dicembre"
  ];

  return (
    <div className="space-y-6">
      {/* Month Reference Header */}
      <Card>
        <CardContent className="pt-6">
          <div className="flex items-center justify-center gap-2">
            <Calendar className="h-5 w-5 text-primary" />
            <span className="text-lg font-semibold">Food Cost - Mese di Riferimento:</span>
            <Badge variant="secondary" className="text-lg px-3 py-1">
              {monthNames[selectedMonth - 1]} {selectedYear}
            </Badge>
          </div>
          <p className="text-center text-sm text-muted-foreground mt-2">
            I dati mostrati si riferiscono al mese selezionato nella dashboard
          </p>
        </CardContent>
      </Card>

      {/* Summary Cards */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <TrendingUp className="h-5 w-5 text-primary" />
            Riepilogo Generale
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
            <Card className="p-4">
              <div className="flex items-center gap-2">
                <DollarSign className="h-4 w-4 text-destructive" />
                <div>
                  <p className="text-sm font-medium text-muted-foreground">
                    Costo Totale Materiali
                  </p>
                  <p className="text-xl font-bold text-destructive font-mono">
                    €{totalCostOfSales.toFixed(1)}
                  </p>
                </div>
              </div>
            </Card>

            <Card className="p-4">
              <div className="flex items-center gap-2">
                <TrendingUp className="h-4 w-4 text-primary" />
                <div>
                  <p className="text-sm font-medium text-muted-foreground">
                    Ricavo Totale
                  </p>
                  <p className="text-sm font-bold text-primary font-mono">
                    €{totalGrossSales.toFixed(1)} / €{totalNetSales.toFixed(1)}
                  </p>
                  <p className="text-xs text-muted-foreground">Lordo / Netto</p>
                </div>
              </div>
            </Card>

            <Card className="p-4">
              <div className="flex items-center gap-2">
                <AlertTriangle className="h-4 w-4 text-destructive" />
                <div>
                  <p className="text-sm font-medium text-muted-foreground">
                    Totale Sprechi
                  </p>
                  <p className="text-xl font-bold text-destructive font-mono">
                    €{totalWasteCost.toFixed(1)}
                  </p>
                </div>
              </div>
            </Card>

            <Card className="p-4">
              <div className="flex items-center gap-2">
                <Users className="h-4 w-4 text-destructive" />
                <div>
                  <p className="text-sm font-medium text-muted-foreground">
                    Totale Pasti Personali
                  </p>
                  <p className="text-xl font-bold text-destructive font-mono">
                    €{totalPersonalMealsCost.toFixed(1)}
                  </p>
                </div>
              </div>
            </Card>
          </div>
        </CardContent>
      </Card>

      {/* Food Cost Analysis */}
      <Card>
        <CardContent className="p-6">
          <div className="space-y-4">
            <div>
              <Label htmlFor="maxFoodCost">Soglia Food Cost</Label>
              <div className="relative mt-1">
                <Input
                  id="maxFoodCost"
                  type="number"
                  min="0"
                  max="100"
                  value={maxFoodCost}
                  onChange={(e) => handleMaxFoodCostChange(e.target.value)}
                  className="pr-8"
                  data-testid="input-max-food-cost"
                />
                <span className="absolute right-3 top-1/2 transform -translate-y-1/2 text-muted-foreground font-medium">
                  %
                </span>
              </div>
            </div>

            <Card className={`p-4 ${isOverThreshold ? 'bg-destructive/10 border-destructive' : 'bg-chart-2/10 border-chart-2'}`}>
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-2">
                  <span className="font-semibold text-foreground">
                    Food Cost Teorico Ponderato:
                  </span>
                  <TooltipProvider>
                    <Tooltip>
                      <TooltipTrigger asChild>
                        <Button
                          variant="ghost"
                          size="icon"
                          className="h-5 w-5 p-0 hover:bg-transparent"
                          data-testid="button-food-cost-info"
                        >
                          <Info className="h-4 w-4 text-muted-foreground hover:text-foreground transition-colors" />
                        </Button>
                      </TooltipTrigger>
                      <TooltipContent className="max-w-md p-4">
                        <div className="space-y-3 text-sm">
                          <p>
                            Il <strong>Food Cost Teorico</strong>, noto anche come Food Cost Preventivo o Attivo, rappresenta il costo ideale che un'attività di ristorazione dovrebbe sostenere per la preparazione di un singolo piatto in condizioni perfette e senza sprechi. A differenza del costo reale, che si calcola a consuntivo, quello teorico viene stabilito in via preventiva, prima che il piatto venga preparato.
                          </p>
                          <p>
                            Questo calcolo si basa su ricette analitiche e standardizzate, che specificano con precisione la quantità esatta di ogni ingrediente e il suo costo unitario. L'obiettivo è creare una "distinta base" che elenchi tutti i componenti necessari per una porzione standard, eliminando la gestione a "occhio".
                          </p>
                          <div>
                            <p className="font-medium mb-2">Il valore che si ottiene è un benchmark che serve a:</p>
                            <ul className="list-disc list-inside space-y-1 pl-2">
                              <li><strong>Determinare il prezzo di vendita</strong>: fornisce una base razionale per definire un prezzo che assicuri il margine di profitto desiderato.</li>
                              <li><strong>Misurare l'efficienza</strong>: viene confrontato con il Food Cost Consuntivo (o reale) per individuare perdite di profitto dovute a sprechi, porzionamento impreciso o altri problemi operativi.</li>
                            </ul>
                          </div>
                        </div>
                      </TooltipContent>
                    </Tooltip>
                  </TooltipProvider>
                </div>
                <span className={`text-2xl font-bold font-mono ${isOverThreshold ? 'text-destructive' : 'text-chart-2'}`}>
                  {weightedFoodCost.toFixed(1)}%
                </span>
              </div>
              {isOverThreshold && (
                <p className="text-sm text-destructive mt-2">
                  ⚠️ Il food cost supera la soglia impostata del {maxFoodCost}%
                </p>
              )}
            </Card>
          </div>
        </CardContent>
      </Card>

      {/* Detailed Sales Breakdown - TODO: Update to use sales table data */}
      {showSalesDetails && false && (
        <Card>
          <CardHeader>
            <CardTitle>Dettaglio Vendite</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              <p className="text-center text-muted-foreground italic">
                Le vendite sono ora gestite nella sezione dedicata "Vendite".
              </p>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/TabNavigation.tsx.content:
import { Button } from "@/components/ui/button";
import { Link, useLocation } from "wouter";
import { 
  LayoutDashboard,
  Warehouse, 
  Calculator, 
  Trash2, 
  TrendingUp, 
  Truck, 
  ArrowUpDown, 
  BarChart3,
  Users,
  TrendingUp as ProfitIcon,
  Calendar,
  ChefHat,
  Utensils
} from "lucide-react";

interface TabNavigationProps {
  activeTab: string;
  onTabChange: (tab: string) => void;
}

// Primary operational tabs
const primaryTabs = [
  { id: "dashboard", label: "Dashboard", icon: LayoutDashboard },
  { id: "budget", label: "Budget", icon: Calendar },
  { id: "profit-loss", label: "P&L", icon: ProfitIcon },
  { id: "food-cost", label: "Food Cost", icon: Calculator },
  { id: "labour-cost", label: "Labour Cost", icon: Users },
  { id: "sales-detail", label: "Vendite", icon: BarChart3 },
];

// Management section tabs (after separator)
const managementTabs = [
  { id: "inventory", label: "Inventario", icon: Warehouse },
  { id: "recipes", label: "Ricette", icon: ChefHat },
  { id: "suppliers", label: "Fornitori", icon: Users },
  { id: "orders", label: "Ordini", icon: Truck },
  { id: "warehouse", label: "Magazzino", icon: ArrowUpDown },
  { id: "waste", label: "Sprechi/Staff Food", icon: Trash2 },
];

export default function TabNavigation({ activeTab, onTabChange }: TabNavigationProps) {  
  const renderTab = (tab: any, key: string) => {
    const Icon = tab.icon;
    const isActive = activeTab === tab.id;

    return (
      <Button
        key={key}
        variant="ghost"
        size="default"
        onClick={() => onTabChange(tab.id)}
        data-testid={`tab-${tab.id}`}
        className={`flex-1 min-w-0 rounded-none py-3 px-2 md:px-4 text-xs md:text-sm hover-elevate transition-colors flex items-center justify-center gap-1 md:gap-2 mobile-touch-target ${
          isActive 
            ? "bg-background border-b-2 border-primary text-primary font-medium" 
            : "text-muted-foreground"
        }`}
        aria-label={tab.label}
        title={tab.label}
      >
        <Icon className="h-4 w-4 flex-shrink-0" />
        <span className="hidden sm:inline mobile-text-large">{tab.label}</span>
      </Button>
    );
  };

  return (
    <nav className="flex bg-muted border-b border-border mobile-table-scroll">
      {/* Primary operational tabs */}
      {primaryTabs.map((tab, index) => renderTab(tab, `primary-${index}`))}

      {/* Visual separator with fork icon */}
      <div className="flex items-center justify-center px-1 md:px-2 flex-shrink-0">
        <Utensils className="h-4 w-4 text-red-500" />
      </div>

      {/* Management section tabs */}
      {managementTabs.map((tab, index) => renderTab(tab, `management-${index}`))}
    </nav>
  );
}

