import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertStockMovementSchema, type InsertStockMovement, type StockMovement, type Product } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { ArrowUpDown, ArrowUp, ArrowDown, Package, Calendar, Euro } from "lucide-react";

interface StockMovementFormProps {
  onSubmit: (movement: InsertStockMovement) => void;
  editMovement?: StockMovement;
  onCancel?: () => void;
  products: Product[];
}

export default function StockMovementForm({ onSubmit, editMovement, onCancel, products }: StockMovementFormProps) {
  const isEditing = !!editMovement;

  const form = useForm<InsertStockMovement>({
    resolver: zodResolver(insertStockMovementSchema),
    defaultValues: {
      productId: "",
      movementType: "in",
      quantity: 0,
      unitPrice: 0,
      totalCost: 0,
      source: "adjustment",
      movementDate: new Date().toISOString().split('T')[0],
      notes: "",
    },
  });

  // Update form values when editMovement changes
  useEffect(() => {
    if (editMovement) {
      form.reset({
        productId: editMovement.productId,
        movementType: editMovement.movementType as "in" | "out",
        quantity: editMovement.quantity,
        unitPrice: editMovement.unitPrice || 0,
        totalCost: editMovement.totalCost || 0,
        source: editMovement.source as "waste" | "order" | "sale" | "personal_meal" | "adjustment",
        movementDate: editMovement.movementDate.split('T')[0],
        notes: editMovement.notes || "",
      });
    } else {
      form.reset({
        productId: "",
        movementType: "in",
        quantity: 0,
        unitPrice: 0,
        totalCost: 0,
        source: "adjustment",
        movementDate: new Date().toISOString().split('T')[0],
        notes: "",
      });
    }
  }, [editMovement, form]);

  // Auto-calculate total cost when quantity or unit price changes
  const watchedQuantity = form.watch("quantity");
  const watchedUnitPrice = form.watch("unitPrice");
  useEffect(() => {
    const total = (watchedQuantity || 0) * (watchedUnitPrice || 0);
    form.setValue("totalCost", total);
  }, [watchedQuantity, watchedUnitPrice, form]);

  const handleSubmit = (data: InsertStockMovement) => {
    console.log("Stock movement form submitted:", data);
    onSubmit(data);
    if (!isEditing) {
      form.reset({
        productId: "",
        movementType: "in",
        quantity: 0,
        unitPrice: 0,
        totalCost: 0,
        source: "adjustment",
        movementDate: new Date().toISOString().split('T')[0],
        notes: "",
      });
    }
  };

  const handleCancel = () => {
    console.log("Stock movement form cancelled");
    form.reset();
    onCancel?.();
  };

  const getProductName = (productId: string) => {
    const product = products.find(p => p.id === productId);
    return product ? `${product.name} (${product.code})` : "";
  };

  const getMovementTypeIcon = (type: string) => {
    switch (type) {
      case "in":
        return <ArrowUp className="h-4 w-4 text-green-600" />;
      case "out":
        return <ArrowDown className="h-4 w-4 text-red-600" />;
      default:
        return <ArrowUpDown className="h-4 w-4" />;
    }
  };

  return (
    <Card>
      <CardHeader className="flex flex-row items-center gap-2">
        <Package className="h-5 w-5 text-primary" />
        <CardTitle>
          {isEditing ? "Modifica Movimento" : "Nuovo Movimento Magazzino"}
        </CardTitle>
      </CardHeader>
      <CardContent>
        <Form {...form}>
          <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-6">
            {/* Product and Movement Type */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="productId"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="flex items-center gap-2">
                      <Package className="h-4 w-4" />
                      Prodotto
                    </FormLabel>
                    <Select 
                      value={field.value} 
                      onValueChange={field.onChange}
                      data-testid="select-product"
                    >
                      <FormControl>
                        <SelectTrigger>
                          <SelectValue placeholder="Seleziona prodotto" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {products.map((product) => (
                          <SelectItem key={product.id} value={product.id}>
                            {product.name} ({product.code})
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="movementType"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="flex items-center gap-2">
                      <ArrowUpDown className="h-4 w-4" />
                      Tipo Movimento
                    </FormLabel>
                    <Select 
                      value={field.value} 
                      onValueChange={field.onChange}
                      data-testid="select-movement-type"
                    >
                      <FormControl>
                        <SelectTrigger>
                          <SelectValue placeholder="Seleziona tipo" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="in">
                          <div className="flex items-center gap-2">
                            <ArrowUp className="h-4 w-4 text-green-600" />
                            Entrata (IN)
                          </div>
                        </SelectItem>
                        <SelectItem value="out">
                          <div className="flex items-center gap-2">
                            <ArrowDown className="h-4 w-4 text-red-600" />
                            Uscita (OUT)
                          </div>
                        </SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            {/* Quantity and Pricing */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <FormField
                control={form.control}
                name="quantity"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Quantità</FormLabel>
                    <FormControl>
                      <Input
                        type="number"
                        min="0"
                        step="0.01"
                        placeholder="0"
                        className="bg-yellow-100 dark:bg-yellow-900/30"
                        data-testid="input-quantity"
                        {...field}
                        onChange={(e) => field.onChange(parseFloat(e.target.value) || 0)}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="unitPrice"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="flex items-center gap-2">
                      <Euro className="h-4 w-4" />
                      Prezzo Unitario (€)
                    </FormLabel>
                    <FormControl>
                      <Input
                        type="number"
                        min="0"
                        step="0.01"
                        placeholder="0.00"
                        className="bg-yellow-100 dark:bg-yellow-900/30"
                        data-testid="input-unit-price"
                        {...field}
                        onChange={(e) => field.onChange(parseFloat(e.target.value) || 0)}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="totalCost"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="flex items-center gap-2">
                      <Euro className="h-4 w-4" />
                      Costo Totale (€)
                    </FormLabel>
                    <FormControl>
                      <Input
                        type="number"
                        min="0"
                        step="0.01"
                        placeholder="0.00"
                        data-testid="input-total-cost"
                        readOnly
                        className="bg-muted"
                        {...field}
                        onChange={(e) => field.onChange(parseFloat(e.target.value) || 0)}
                      />
                    </FormControl>
                    <div className="text-sm text-muted-foreground">
                      Calcolato automaticamente
                    </div>
                  </FormItem>
                )}
              />
            </div>

            {/* Date and Source */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="movementDate"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="flex items-center gap-2">
                      <Calendar className="h-4 w-4" />
                      Data Movimento
                    </FormLabel>
                    <FormControl>
                      <Input 
                        type="date" 
                        className="bg-yellow-100 dark:bg-yellow-900/30"
                        data-testid="input-movement-date"
                        {...field} 
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="source"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Origine Movimento</FormLabel>
                    <Select 
                      value={field.value} 
                      onValueChange={field.onChange}
                      data-testid="select-source"
                    >
                      <FormControl>
                        <SelectTrigger>
                          <SelectValue placeholder="Seleziona origine" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="order">Ordine</SelectItem>
                        <SelectItem value="sale">Vendita</SelectItem>
                        <SelectItem value="waste">Scarto</SelectItem>
                        <SelectItem value="adjustment">Aggiustamento</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            {/* Notes */}
            <FormField
              control={form.control}
              name="notes"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Note</FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder="Note aggiuntive sul movimento..."
                      rows={3}
                      value={field.value || ""}
                      onChange={field.onChange}
                      onBlur={field.onBlur}
                      name={field.name}
                      ref={field.ref}
                      className="bg-yellow-100 dark:bg-yellow-900/30"
                      data-testid="input-notes"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Form Actions */}
            <div className="flex gap-3 pt-4">
              <Button 
                type="submit" 
                data-testid="button-submit-movement"
                className="flex-1 md:flex-none flex items-center gap-2"
              >
                {getMovementTypeIcon(form.watch("movementType"))}
                {isEditing ? "Aggiorna Movimento" : "Registra Movimento"}
              </Button>
              {isEditing && (
                <Button 
                  type="button" 
                  variant="outline" 
                  onClick={handleCancel}
                  data-testid="button-cancel-movement"
                >
                  Annulla
                </Button>
              )}
            </div>
          </form>
        </Form>
      </CardContent>
    </Card>
  );
}

client/src/components/StockMovementList..content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { 
  Edit2, 
  Trash2, 
  ArrowUp,
  ArrowDown,
  Package,
  Calendar,
  User,
  FileText,
  Euro,
  Filter,
  X
} from "lucide-react";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import type { StockMovement, Product } from "@shared/schema";

interface StockMovementListProps {
  movements: StockMovement[];
  products: Product[];
  onEdit: (movement: StockMovement) => void;
  onDelete: (movementId: string) => void;
  selectedProductId?: string;
  onFilterByProduct?: (productId: string | null) => void;
}

export default function StockMovementList({ 
  movements, 
  products, 
  onEdit, 
  onDelete,
  selectedProductId,
  onFilterByProduct 
}: StockMovementListProps) {
  const [sortBy, setSortBy] = useState<"date" | "product" | "type">("date");
  const [sortOrder, setSortOrder] = useState<"asc" | "desc">("desc");

  const getProductName = (productId: string) => {
    const product = products.find(p => p.id === productId);
    return product ? `${product.name} (${product.code})` : "Prodotto non trovato";
  };

  const getMovementTypeColor = (type: string) => {
    switch (type) {
      case "in":
        return "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-100";
      case "out":
        return "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-100";
      default:
        return "bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-100";
    }
  };

  const getMovementTypeLabel = (type: string) => {
    switch (type) {
      case "in":
        return "Entrata";
      case "out":
        return "Uscita";
      default:
        return type;
    }
  };

  const getMovementTypeIcon = (type: string) => {
    switch (type) {
      case "in":
        return <ArrowUp className="h-4 w-4" />;
      case "out":
        return <ArrowDown className="h-4 w-4" />;
      default:
        return null;
    }
  };

  const getSourceLabel = (source: string) => {
    switch (source) {
      case "order":
        return "Ordine";
      case "sale":
        return "Vendita";
      case "waste":
        return "Scarto";
      case "adjustment":
        return "Aggiustamento";
      default:
        return source;
    }
  };

  // Filter movements
  const filteredMovements = selectedProductId 
    ? movements.filter(m => m.productId === selectedProductId)
    : movements;

  // Sort movements
  const sortedMovements = [...filteredMovements].sort((a, b) => {
    let comparison = 0;

    switch (sortBy) {
      case "date":
        comparison = new Date(a.movementDate).getTime() - new Date(b.movementDate).getTime();
        break;
      case "product":
        comparison = getProductName(a.productId).localeCompare(getProductName(b.productId));
        break;
      case "type":
        comparison = a.movementType.localeCompare(b.movementType);
        break;
    }

    return sortOrder === "desc" ? -comparison : comparison;
  });

  const getTotals = () => {
    const totalIn = sortedMovements
      .filter(m => m.movementType === "in")
      .reduce((sum, m) => sum + m.quantity, 0);
    const totalOut = sortedMovements
      .filter(m => m.movementType === "out")
      .reduce((sum, m) => sum + m.quantity, 0);
    const totalValue = sortedMovements
      .reduce((sum, m) => sum + (m.totalCost || (m.quantity * (m.unitPrice || 0))), 0);

    return { totalIn, totalOut, totalValue };
  };

  const totals = getTotals();

  if (movements.length === 0) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Package className="h-5 w-5" />
            Movimenti di Magazzino
          </CardTitle>
        </CardHeader>
        <CardContent className="text-center py-12">
          <Package className="h-12 w-12 mx-auto text-muted-foreground mb-4" />
          <p className="text-muted-foreground">Nessun movimento registrato</p>
          <p className="text-sm text-muted-foreground mt-1">
            Registra il primo movimento per iniziare il tracking
          </p>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-2">
            <Package className="h-5 w-5" />
            Movimenti di Magazzino ({sortedMovements.length})
          </CardTitle>
          <div className="flex items-center gap-2">
            {/* Filter */}
            {onFilterByProduct && (
              <div className="flex items-center gap-2">
                <Filter className="h-4 w-4" />
                <Select 
                  value={selectedProductId || "all"} 
                  onValueChange={(value) => onFilterByProduct(value === "all" ? null : value)}
                >
                  <SelectTrigger className="w-48" data-testid="select-filter-product">
                    <SelectValue placeholder="Filtra per prodotto" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">Tutti i prodotti</SelectItem>
                    {products.map((product) => (
                      <SelectItem key={product.id} value={product.id}>
                        {product.name} ({product.code})
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                {selectedProductId && (
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onFilterByProduct(null)}
                    data-testid="button-clear-filter"
                  >
                    <X className="h-4 w-4" />
                  </Button>
                )}
              </div>
            )}

            {/* Sort */}
            <Select value={sortBy} onValueChange={(value) => setSortBy(value as any)}>
              <SelectTrigger className="w-32" data-testid="select-sort-by">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="date">Data</SelectItem>
                <SelectItem value="product">Prodotto</SelectItem>
                <SelectItem value="type">Tipo</SelectItem>
              </SelectContent>
            </Select>

            <Button
              variant="outline"
              size="sm"
              onClick={() => setSortOrder(sortOrder === "asc" ? "desc" : "asc")}
              data-testid="button-sort-order"
            >
              {sortOrder === "asc" ? "↑" : "↓"}
            </Button>
          </div>
        </div>

        {/* Summary Stats */}
        {sortedMovements.length > 0 && (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-4">
            <div className="flex items-center gap-2 text-sm">
              <ArrowUp className="h-4 w-4 text-green-600" />
              <span>Entrate: <span className="font-semibold" data-testid="total-in">{totals.totalIn.toFixed(1)}</span></span>
            </div>
            <div className="flex items-center gap-2 text-sm">
              <ArrowDown className="h-4 w-4 text-red-600" />
              <span>Uscite: <span className="font-semibold" data-testid="total-out">{totals.totalOut.toFixed(1)}</span></span>
            </div>
            <div className="flex items-center gap-2 text-sm">
              <Euro className="h-4 w-4 text-primary" />
              <span>Valore: <span className="font-semibold" data-testid="total-value">€{totals.totalValue.toFixed(1)}</span></span>
            </div>
          </div>
        )}
      </CardHeader>

      <CardContent className="space-y-4">
        {sortedMovements.map((movement) => (
          <Card key={movement.id} className="border">
            <CardContent className="p-4">
              <div className="flex items-start justify-between mb-3">
                <div className="flex-1">
                  <div className="flex items-center gap-2 mb-2">
                    <h3 className="font-medium" data-testid={`movement-product-${movement.id}`}>
                      {getProductName(movement.productId)}
                    </h3>
                    <Badge 
                      className={getMovementTypeColor(movement.movementType)}
                      data-testid={`movement-type-${movement.id}`}
                    >
                      <div className="flex items-center gap-1">
                        {getMovementTypeIcon(movement.movementType)}
                        {getMovementTypeLabel(movement.movementType)}
                      </div>
                    </Badge>
                    <Badge variant="outline" data-testid={`movement-source-${movement.id}`}>
                      {getSourceLabel(movement.source)}
                    </Badge>
                  </div>

                  <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-2 text-sm text-muted-foreground">
                    <div className="flex items-center gap-1">
                      <Calendar className="h-4 w-4" />
                      <span data-testid={`movement-date-${movement.id}`}>
                        {new Date(movement.movementDate).toLocaleDateString('it-IT')}
                      </span>
                    </div>

                    <div className="flex items-center gap-1">
                      <Package className="h-4 w-4" />
                      <span data-testid={`movement-quantity-${movement.id}`}>
                        {movement.quantity} unità
                      </span>
                    </div>

                    {movement.unitPrice && movement.unitPrice > 0 && (
                      <div className="flex items-center gap-1">
                        <Euro className="h-4 w-4" />
                        <span data-testid={`movement-unit-price-${movement.id}`}>
                          €{movement.unitPrice.toFixed(2)}/unità
                        </span>
                      </div>
                    )}

                    {movement.totalCost && movement.totalCost > 0 && (
                      <div className="flex items-center gap-1">
                        <Euro className="h-4 w-4" />
                        <span className="font-semibold" data-testid={`movement-total-${movement.id}`}>
                          €{movement.totalCost.toFixed(2)}
                        </span>
                      </div>
                    )}
                  </div>

                  {movement.notes && (
                    <div className="flex items-start gap-1 mt-2 text-sm text-muted-foreground">
                      <FileText className="h-4 w-4 mt-0.5" />
                      <span data-testid={`movement-notes-${movement.id}`}>
                        {movement.notes}
                      </span>
                    </div>
                  )}
                </div>

                <div className="flex items-center gap-1 ml-4">
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onEdit(movement)}
                    data-testid={`button-edit-movement-${movement.id}`}
                  >
                    <Edit2 className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onDelete(movement.id)}
                    className="text-destructive hover:text-destructive"
                    data-testid={`button-delete-movement-${movement.id}`}
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </CardContent>
    </Card>
  );
}

client/src/components/WasteRegistry.tsx.content:
import { useState, useMemo } from "react";
import { type Waste, type PersonalMeal, type Product, type Dish } from "@shared/schema";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Button } from "@/components/ui/button";
import { CalendarDays, Trash2, Users, Search, Filter, X } from "lucide-react";
import { format, parseISO } from "date-fns";
import { it } from "date-fns/locale";
import { useDeleteWaste, useDeletePersonalMeal } from "@/hooks/useApi";

interface WasteRegistryProps {
  waste: Waste[];
  personalMeals: PersonalMeal[];
  products: Product[];
  dishes: Dish[];
}

export default function WasteRegistry({ waste, personalMeals, products, dishes }: WasteRegistryProps) {
  const [dateFilter, setDateFilter] = useState("");
  const [minCost, setMinCost] = useState("");
  const [maxCost, setMaxCost] = useState("");
  const [productFilter, setProductFilter] = useState("");

  // Delete mutations
  const deleteWasteMutation = useDeleteWaste();
  const deletePersonalMealMutation = useDeletePersonalMeal();

  const handleDeleteItem = (item: any) => {
    if (item.type === "waste") {
      deleteWasteMutation.mutate(item.id);
    } else {
      deletePersonalMealMutation.mutate(item.id);
    }
  };

  // Combine waste and personal meals into a single list with type distinction
  const combinedItems = useMemo(() => {
    const wasteItems = waste.map(w => ({
      ...w,
      type: "waste" as const,
      itemName: products.find(p => p.id === w.productId)?.name || "Prodotto sconosciuto",
      displayDate: w.date,
    }));

    const mealItems = personalMeals.map(m => ({
      ...m,
      type: "meal" as const,
      itemName: dishes.find(d => d.id === m.dishId)?.name || "Piatto sconosciuto",
      displayDate: m.date,
      productId: m.dishId, // Use dishId for filtering consistency
    }));

    return [...wasteItems, ...mealItems].sort((a, b) => 
      new Date(b.displayDate).getTime() - new Date(a.displayDate).getTime()
    );
  }, [waste, personalMeals, products, dishes]);

  // Apply filters
  const filteredItems = useMemo(() => {
    return combinedItems.filter(item => {
      // Date filter
      if (dateFilter && item.displayDate !== dateFilter) {
        return false;
      }

      // Cost range filter
      if (minCost && item.cost < parseFloat(minCost)) {
        return false;
      }
      if (maxCost && item.cost > parseFloat(maxCost)) {
        return false;
      }

      // Product/Dish name filter
      if (productFilter && !item.itemName.toLowerCase().includes(productFilter.toLowerCase())) {
        return false;
      }

      return true;
    });
  }, [combinedItems, dateFilter, minCost, maxCost, productFilter]);

  // Calculate totals
  const totalWasteCost = filteredItems
    .filter(item => item.type === "waste")
    .reduce((sum, item) => sum + item.cost, 0);
  const totalMealCost = filteredItems
    .filter(item => item.type === "meal")
    .reduce((sum, item) => sum + item.cost, 0);

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <CalendarDays className="h-5 w-5 text-primary" />
          Registro Sprechi e Pasti Personali
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Filters */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 p-4 bg-muted rounded-lg">
          <div>
            <label className="text-sm font-medium text-muted-foreground mb-2 block">
              Filtra per data
            </label>
            <Input
              type="date"
              value={dateFilter}
              onChange={(e) => setDateFilter(e.target.value)}
              className="w-full"
              data-testid="input-date-filter"
            />
          </div>

          <div>
            <label className="text-sm font-medium text-muted-foreground mb-2 block">
              Costo minimo (€)
            </label>
            <Input
              type="number"
              step="0.01"
              min="0"
              placeholder="0.00"
              value={minCost}
              onChange={(e) => setMinCost(e.target.value)}
              data-testid="input-min-cost-filter"
            />
          </div>

          <div>
            <label className="text-sm font-medium text-muted-foreground mb-2 block">
              Costo massimo (€)
            </label>
            <Input
              type="number"
              step="0.01"
              min="0"
              placeholder="∞"
              value={maxCost}
              onChange={(e) => setMaxCost(e.target.value)}
              data-testid="input-max-cost-filter"
            />
          </div>

          <div>
            <label className="text-sm font-medium text-muted-foreground mb-2 block">
              Nome prodotto/piatto
            </label>
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
              <Input
                placeholder="Cerca..."
                value={productFilter}
                onChange={(e) => setProductFilter(e.target.value)}
                className="pl-10"
                data-testid="input-product-filter"
              />
            </div>
          </div>
        </div>

        {/* Summary Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <Card className="p-4">
            <div className="flex items-center gap-2">
              <Trash2 className="h-4 w-4 text-destructive" />
              <div>
                <p className="text-sm font-medium text-muted-foreground">
                  Totale Sprechi
                </p>
                <p className="text-xl font-bold text-destructive font-mono">
                  €{totalWasteCost.toFixed(2)}
                </p>
              </div>
            </div>
          </Card>

          <Card className="p-4">
            <div className="flex items-center gap-2">
              <Users className="h-4 w-4 text-primary" />
              <div>
                <p className="text-sm font-medium text-muted-foreground">
                  Totale Pasti Personali
                </p>
                <p className="text-xl font-bold text-primary font-mono">
                  €{totalMealCost.toFixed(2)}
                </p>
              </div>
            </div>
          </Card>

          <Card className="p-4">
            <div className="flex items-center gap-2">
              <Filter className="h-4 w-4 text-chart-2" />
              <div>
                <p className="text-sm font-medium text-muted-foreground">
                  Elementi Filtrati
                </p>
                <p className="text-xl font-bold text-chart-2 font-mono">
                  {filteredItems.length}
                </p>
              </div>
            </div>
          </Card>
        </div>

        {/* Registry List */}
        <div>
          <h3 className="font-semibold text-foreground mb-4">
            Registro ({filteredItems.length} elementi)
          </h3>

          {filteredItems.length === 0 ? (
            <Card className="p-8 text-center">
              <p className="text-muted-foreground">
                Nessun elemento trovato con i filtri applicati.
              </p>
            </Card>
          ) : (
            <ScrollArea className="h-96 w-full">
              <div className="space-y-2">
                {filteredItems.map((item, index) => (
                  <Card key={`${item.type}-${item.id || index}`} className="p-4">
                    <div className="flex justify-between items-start">
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-2">
                          {item.type === "waste" ? (
                            <Trash2 className="h-4 w-4 text-destructive" />
                          ) : (
                            <Users className="h-4 w-4 text-primary" />
                          )}
                          <Badge variant={item.type === "waste" ? "destructive" : "default"}>
                            {item.type === "waste" ? "Spreco" : "Pasto Personale"}
                          </Badge>
                        </div>

                        <h4 className="font-medium text-foreground">
                          {item.itemName}
                        </h4>

                        <div className="text-sm text-muted-foreground mt-1">
                          <div>
                            Quantità: <span className="font-mono">{item.quantity}</span>
                            {item.type === "waste" && (
                              <>
                                {" "}
                                {products.find(p => p.id === item.productId)?.unit || ""}
                              </>
                            )}
                          </div>
                          <div>
                            Data: {format(parseISO(item.displayDate), "dd/MM/yyyy", { locale: it })}
                          </div>
                          {item.notes && (
                            <div className="mt-1 text-xs">
                              Note: {item.notes}
                            </div>
                          )}
                        </div>
                      </div>

                      <div className="flex items-center gap-2">
                        <div className="text-right">
                          <div className="text-lg font-bold font-mono text-destructive">
                            €{item.cost.toFixed(2)}
                          </div>
                        </div>
                        <Button
                          variant="outline"
                          size="icon"
                          onClick={() => handleDeleteItem(item)}
                          className="h-8 w-8 text-destructive hover:text-destructive"
                          data-testid={`button-delete-${item.type}-${item.id}`}
                        >
                          <X className="h-4 w-4" />
                        </Button>
                      </div>
                    </div>
                  </Card>
                ))}
              </div>
            </ScrollArea>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/utils/pdfExport.ts.content:
import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';
import type { Product, StockMovement, Order, Waste, PersonalMeal, Recipe, Dish, EditableInventory, BudgetEntry } from '@shared/schema';

// Universal PDF export interface
interface PDFExportOptions {
  title: string;
  subtitle?: string;
  data: any[];
  columns: { header: string; dataKey: string; width?: number }[];
  filename: string;
  orientation?: 'portrait' | 'landscape';
  showDate?: boolean;
  footerText?: string;
}

// Enhanced PDF header setup
const setupPDFHeader = (doc: jsPDF, title: string, subtitle?: string) => {
  // Calculate center X position dynamically
  const centerX = doc.internal.pageSize.width / 2;

  // Header
  doc.setFontSize(20);
  doc.setFont("helvetica", "bold");
  doc.text("FoodyFlow", centerX, 20, { align: "center" });

  doc.setFontSize(16);
  doc.setFont("helvetica", "bold");
  doc.text(title, centerX, 30, { align: "center" });

  if (subtitle) {
    doc.setFontSize(12);
    doc.setFont("helvetica", "normal");
    doc.text(subtitle, centerX, 40, { align: "center" });
  }

  // Date
  doc.setFontSize(10);
  doc.setFont("helvetica", "normal");
  const date = new Date().toLocaleDateString('it-IT', {
    year: 'numeric',
    month: 'long', 
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit'
  });
  doc.text(`Data generazione: ${date}`, centerX, subtitle ? 50 : 40, { align: "center" });

  // Add line separator
  const startY = subtitle ? 60 : 50;
  doc.setLineWidth(0.5);
  doc.line(20, startY, doc.internal.pageSize.width - 20, startY);

  return startY + 10;
};

// Unified footer system for all PDF reports  
const addUnifiedFooter = (doc: jsPDF, footerText?: string): void => {
  const totalPages = doc.getNumberOfPages();
  const pageHeight = doc.internal.pageSize.height;
  const centerX = doc.internal.pageSize.width / 2;

  for (let i = 1; i <= totalPages; i++) {
    doc.setPage(i);
    doc.setFontSize(8);
    doc.setFont("helvetica", "normal");

    if (footerText) {
      doc.text(footerText, centerX, pageHeight - 15, { align: "center" });
    }

    const pageNumber = `Pagina ${i} di ${totalPages}`;
    doc.text(pageNumber, doc.internal.pageSize.width - 25, pageHeight - 10);
  }
};

// Universal table export function
export const exportTableToPDF = (options: PDFExportOptions): void => {
  const doc = new jsPDF({
    orientation: options.orientation || 'portrait',
    unit: 'mm',
    format: 'a4'
  });

  const startY = setupPDFHeader(doc, options.title, options.subtitle);

  // Prepare table data
  const tableData = options.data.map(row => 
    options.columns.map(col => {
      const value = row[col.dataKey];
      if (value === null || value === undefined) return '';
      if (typeof value === 'number') {
        // Format currency and percentages based on header content
        if (col.header.includes('€') || col.header.includes('Budget') || col.header.includes('Incasso') || col.header.includes('C.M.')) {
          return `€${value.toFixed(2)}`;
        }
        if (col.header.includes('%') || col.dataKey.includes('Percentage') || col.dataKey.includes('Delta')) {
          return `${value.toFixed(1)}%`;
        }
        return value.toFixed(2);
      }
      return String(value);
    })
  );

  // Generate table using autoTable
  autoTable(doc, {
    head: [options.columns.map(col => col.header)],
    body: tableData,
    startY: startY,
    margin: { left: 15, right: 15 },
    styles: {
      fontSize: 8,
      cellPadding: 2,
    },
    headStyles: {
      fillColor: [45, 90, 61], // FoodyFlow green
      textColor: [255, 255, 255],
      fontStyle: 'bold',
    },
    alternateRowStyles: {
      fillColor: [245, 245, 245],
    },
    columnStyles: options.columns.reduce((acc, col, index) => {
      if (col.width) {
        acc[index] = { cellWidth: col.width };
      }
      return acc;
    }, {} as any),
  });

  // Use unified footer system
  addUnifiedFooter(doc, options.footerText);

  // Save the PDF
  doc.save(options.filename);
};

// Export Budget report with calculations
export const exportBudgetToPDF = (budgetEntries: BudgetEntry[], selectedYear: number, selectedMonth: number): void => {
  const monthNames = [
    'Gennaio', 'Febbraio', 'Marzo', 'Aprile', 'Maggio', 'Giugno',
    'Luglio', 'Agosto', 'Settembre', 'Ottobre', 'Novembre', 'Dicembre'
  ];

  const columns = [
    { header: 'Giorno', dataKey: 'day', width: 15 },
    { header: 'C.M. €', dataKey: 'copertoMedio', width: 20 },
    { header: 'Coperti', dataKey: 'coperti', width: 20 },
    { header: 'Sala Budget €', dataKey: 'salaBudget', width: 25 },
    { header: 'Delivery Budget €', dataKey: 'budgetDelivery', width: 30 },
    { header: 'Sala Incasso 25 €', dataKey: 'actualRevenue', width: 30 },
    { header: 'Delivery 25 €', dataKey: 'actualDelivery', width: 25 },
    { header: 'Consuntivo 26', dataKey: 'consuntivo', width: 25 },
    { header: 'Consuntivo 25', dataKey: 'consuntivo2025', width: 25 },
    { header: 'Delta %', dataKey: 'deltaPercentage', width: 20 }
  ];

  const exportData = budgetEntries.map(entry => {
    const salaBudget = (entry.coperti || 0) * (entry.copertoMedio || 0);
    const consuntivo2025 = (entry.actualRevenue || 0) + (entry.actualDelivery || 0);
    const consuntivo2026 = entry.consuntivo || 0;
    const deltaPercentage = consuntivo2025 > 0 ? ((consuntivo2026 - consuntivo2025) / consuntivo2025) * 100 : 0;

    return {
      day: entry.day,
      copertoMedio: entry.copertoMedio || 0,
      coperti: entry.coperti || 0,
      salaBudget: salaBudget,
      budgetDelivery: entry.budgetDelivery || 0,
      actualRevenue: entry.actualRevenue || 0,
      actualDelivery: entry.actualDelivery || 0,
      consuntivo: consuntivo2026,
      consuntivo2025: consuntivo2025,
      deltaPercentage: deltaPercentage
    };
  });

  exportTableToPDF({
    title: 'Budget Report',
    subtitle: `${monthNames[selectedMonth - 1]} ${selectedYear}`,
    data: exportData,
    columns: columns,
    filename: `budget-${monthNames[selectedMonth - 1].toLowerCase()}-${selectedYear}.pdf`,
    orientation: 'landscape',
    footerText: 'FoodyFlow - Sistema di Gestione Ristorante'
  });
};

// Export Dashboard KPI report
export const exportDashboardToPDF = (
  totalRevenue: number,
  totalFoodCost: number,
  foodCostPercentage: number,
  ebitda: number,
  ebitdaPercentage: number,
  selectedYear: number,
  selectedMonth: number
): void => {
  const monthNames = [
    'Gennaio', 'Febbraio', 'Marzo', 'Aprile', 'Maggio', 'Giugno',
    'Luglio', 'Agosto', 'Settembre', 'Ottobre', 'Novembre', 'Dicembre'
  ];

  const doc = new jsPDF();
  const startY = setupPDFHeader(doc, 'Dashboard KPI Report', `${monthNames[selectedMonth - 1]} ${selectedYear}`);

  // KPI Cards
  doc.setFontSize(14);
  doc.setFont("helvetica", "bold");
  doc.text('Indicatori Chiave di Performance', 20, startY);

  let currentY = startY + 15;

  // Revenue section
  doc.setFontSize(12);
  doc.setFont("helvetica", "bold");
  doc.text('Ricavi Totali', 20, currentY);
  doc.setFont("helvetica", "normal");
  doc.text(`€${totalRevenue.toFixed(2)}`, 120, currentY);
  currentY += 12;

  // Food Cost section
  doc.setFont("helvetica", "bold");
  doc.text('Costo del Cibo', 20, currentY);
  doc.setFont("helvetica", "normal");
  doc.text(`€${totalFoodCost.toFixed(2)} (${foodCostPercentage.toFixed(1)}%)`, 120, currentY);
  currentY += 12;

  // EBITDA section
  doc.setFont("helvetica", "bold");
  doc.text('EBITDA', 20, currentY);
  doc.setFont("helvetica", "normal");
  doc.text(`€${ebitda.toFixed(2)} (${ebitdaPercentage.toFixed(1)}%)`, 120, currentY);
  currentY += 20;

  // Performance Analysis
  doc.setFontSize(12);
  doc.setFont("helvetica", "bold");
  doc.text('Analisi Performance', 20, currentY);
  currentY += 10;

  doc.setFontSize(10);
  doc.setFont("helvetica", "normal");

  // Food cost analysis
  if (foodCostPercentage <= 30) {
    doc.text('• Food Cost ottimale (≤30%)', 25, currentY);
  } else if (foodCostPercentage <= 35) {
    doc.text('• Food Cost accettabile (30-35%)', 25, currentY);
  } else {
    doc.text('• Food Cost elevato (>35%) - attenzione', 25, currentY);
  }
  currentY += 8;

  // EBITDA analysis
  if (ebitdaPercentage >= 15) {
    doc.text('• EBITDA eccellente (≥15%)', 25, currentY);
  } else if (ebitdaPercentage >= 10) {
    doc.text('• EBITDA buono (10-15%)', 25, currentY);
  } else {
    doc.text('• EBITDA basso (<10%) - miglioramenti necessari', 25, currentY);
  }

  // Use unified footer system after content is complete
  addUnifiedFooter(doc, 'FoodyFlow - Dashboard Report');

  doc.save(`dashboard-kpi-${monthNames[selectedMonth - 1].toLowerCase()}-${selectedYear}.pdf`);
};

