  const totalCostPercentConsuntivo = costItems.reduce((sum, item) => sum + item.consuntivoPercent, 0);
  const ebitdaPercentConsuntivo = 1 - totalCostPercentConsuntivo;

  // Calculate actual EBITDA values in euros
  const totalCostsBudgetEuros = costItems.reduce((sum, item) => sum + (item.budgetValue || 0), 0);
  const totalCostsConsuntivoEuros = costItems.reduce((sum, item) => sum + (item.consuntivoValue || 0), 0);
  const ebitdaBudgetEuros = totals.totalBudget - totalCostsBudgetEuros;
  const ebitdaConsuntivoEuros = (totals.totalConsuntivo) - totalCostsConsuntivoEuros;

  // Export to PDF function
  const handleExportPDF = () => {
    if (!ecoParams || !foodCostMetrics) return;

    exportPLToPDF(
      ecoParams,
      totals,
      foodCostMetrics,
      selectedYear,
      selectedMonth
    );
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="space-y-4">
        <h1 className="text-3xl font-bold">Conto Economico (P&L)</h1>
        <p className="text-muted-foreground">
          Gestione completa del conto economico con analisi dei costi e margini
        </p>

        {/* Month/Year Selector */}
        <div className="flex items-center gap-4 bg-card p-4 rounded-lg">
          <Calendar className="w-5 h-5 text-muted-foreground" />
          <div className="flex items-center gap-2">
            <Select 
              value={selectedMonth.toString()} 
              onValueChange={(value) => setSelectedMonth(parseInt(value))}
              data-testid="select-month"
            >
              <SelectTrigger className="w-40">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                {monthNames.map((month, index) => (
                  <SelectItem key={index + 1} value={(index + 1).toString()}>
                    {month}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>

            <Select 
              value={selectedYear.toString()} 
              onValueChange={(value) => setSelectedYear(parseInt(value))}
              data-testid="select-year"
            >
              <SelectTrigger className="w-24">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                {[2024, 2025, 2026, 2027].map((year) => (
                  <SelectItem key={year} value={year.toString()}>
                    {year}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
          <Badge variant="outline">
            {monthNames[selectedMonth - 1]} {selectedYear}
          </Badge>
          <Button
            variant="outline"
            size="sm"
            onClick={handleExportPDF}
            className="ml-auto"
            data-testid="button-export-pdf"
          >
            <Download className="h-4 w-4 mr-2" />
            Export PDF
          </Button>
        </div>
      </div>

      {/* Economic Table */}
      <Card>
        <CardHeader>
          <CardTitle>Conto Economico Dettagliato</CardTitle>
          <CardDescription>
            Analisi economica con Target %, Budget €, Consuntivo € e Consuntivo % per tutte le voci di costo.
            Integrazione diretta con i dati di Food Cost dalla Dashboard.
          </CardDescription>
        </CardHeader>
        <CardContent className="p-0">
          <div className="mobile-table-scroll">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead className="w-48 md:w-80 text-xs md:text-sm mobile-text-large">Voce</TableHead>
                  <TableHead className="text-center w-16 md:w-20 text-xs md:text-sm">Target %</TableHead>
                  <TableHead className="text-right w-20 md:w-32 text-xs md:text-sm">Budget €</TableHead>
                  <TableHead className="text-right w-20 md:w-32 text-xs md:text-sm">Consuntivo €</TableHead>
                  <TableHead className="text-center w-16 md:w-20 text-xs md:text-sm">Consuntivo %</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {/* Revenue Row */}
                <TableRow className="bg-blue-50 dark:bg-blue-950/20 font-semibold">
                  <TableCell>0010 - Corrispettivi</TableCell>
                  <TableCell className="text-center">100,00%</TableCell>
                  <TableCell className="text-right" data-testid="eco-corrispettivi-budget">{formatCurrency(totals.totalBudget)}</TableCell>
                  <TableCell className="text-right" data-testid="eco-corrispettivi-consuntivo">{formatCurrency(totals.totalConsuntivo)}</TableCell>
                  <TableCell className="text-center">100,00%</TableCell>
                </TableRow>

                {/* Cost Items */}
                {costItems.map((item) => (
                  <TableRow key={item.code} className={item.highlight ? "bg-yellow-50 dark:bg-yellow-950/20" : ""}>
                    <TableCell className={item.highlight ? "font-medium" : ""}>{item.code} - {item.name}</TableCell>
                    {/* Target % Column */}
                    <TableCell 
                      className={`text-center ${item.highlight ? "font-medium" : ""} ${item.isBidirectional ? 'cursor-pointer hover:bg-blue-50 dark:hover:bg-blue-900/20 bg-yellow-100 dark:bg-yellow-900/30' : ''}`}
                      data-testid={item.dataTestId ? `${item.dataTestId}-percent` : undefined}
                      onClick={item.isBidirectional ? () => {
                        const currentValue = item.percent * 100;
                        const percentField = item.field === 'materieFirsteBudget' ? 'materieFirstePercent' : 
                                            item.field === 'acquistiVarBudget' ? 'acquistiVarPercent' : item.field;
                        handleEcoEdit(percentField as keyof UpdateEconomicParameters, currentValue);
                      } : undefined}
                    >
                      {item.isBidirectional && (
                        (ecoEditingField === 'materieFirstePercent' && item.field === 'materieFirsteBudget') ||
                        (ecoEditingField === 'acquistiVarPercent' && item.field === 'acquistiVarBudget')
                      ) ? (
                        <Input
                          value={ecoTempValue}
                          onChange={(e) => setEcoTempValue(e.target.value)}
                          onBlur={() => {
                            const percentField = item.field === 'materieFirsteBudget' ? 'materieFirstePercent' : 
                                                item.field === 'acquistiVarBudget' ? 'acquistiVarPercent' : item.field;
                            handleEcoSave(percentField as keyof UpdateEconomicParameters);
                            setEcoEditingField(null);
                          }}
                          onKeyPress={(e) => {
                            if (e.key === 'Enter') {
                              const percentField = item.field === 'materieFirsteBudget' ? 'materieFirstePercent' : 
                                                  item.field === 'acquistiVarBudget' ? 'acquistiVarPercent' : item.field;
                              handleEcoSave(percentField as keyof UpdateEconomicParameters);
                              setEcoEditingField(null);
                            }
                          }}
                          className="h-6 text-center"
                          autoFocus
                        />
                      ) : (
                        formatPercent(item.percent * 100)
                      )}
                    </TableCell>
                    {/* Budget € Column */}
                    <TableCell 
                      className={`text-right ${item.highlight ? "font-medium" : ""} ${!item.isBidirectional && item.field ? 'cursor-pointer hover:bg-blue-50 dark:hover:bg-blue-900/20 bg-yellow-100 dark:bg-yellow-900/30' : ''}`}
                      data-testid={item.dataTestId ? `${item.dataTestId}-budget` : undefined}
                      onClick={!item.isBidirectional && item.field ? () => {
                        const currentValue = item.budgetValue || 0;
                        handleEcoEdit(item.field as keyof UpdateEconomicParameters, currentValue);
                      } : undefined}
                    >
                      {ecoEditingField === item.field && !item.isBidirectional ? (
                        <Input
                          value={ecoTempValue}
                          onChange={(e) => setEcoTempValue(e.target.value)}
                          onBlur={() => {
                            handleEcoSave(item.field as keyof UpdateEconomicParameters);
                            setEcoEditingField(null);
                          }}
                          onKeyPress={(e) => {
                            if (e.key === 'Enter') {
                              handleEcoSave(item.field as keyof UpdateEconomicParameters);
                              setEcoEditingField(null);
                            }
                          }}
                          className="h-6 text-right"
                          autoFocus
                        />
                      ) : (
                        formatCurrency(item.budgetValue || 0)
                      )}
                    </TableCell>
                    {/* Consuntivo € Column */}
                    <TableCell 
                      className={`text-right ${item.highlight ? "font-medium" : ""} ${item.editable && item.consuntivoField ? 'cursor-pointer hover:bg-blue-50 dark:hover:bg-blue-900/20 bg-yellow-100 dark:bg-yellow-900/30' : ''}`}
                      data-testid={item.dataTestId ? `${item.dataTestId}-consuntivo` : undefined}
                      onClick={item.editable && item.consuntivoField ? () => {
                        const currentValue = item.consuntivoValue || 0;
                        handleEcoEdit(item.consuntivoField as keyof UpdateEconomicParameters, currentValue);
                      } : undefined}
                    >
                      {ecoEditingField === item.consuntivoField && item.editable && item.consuntivoField ? (
                        <Input
                          value={ecoTempValue}
                          onChange={(e) => setEcoTempValue(e.target.value)}
                          onBlur={() => {
                            handleEcoSave(item.consuntivoField as keyof UpdateEconomicParameters);
                            setEcoEditingField(null);
                          }}
                          onKeyPress={(e) => {
                            if (e.key === 'Enter') {
                              handleEcoSave(item.consuntivoField as keyof UpdateEconomicParameters);
                              setEcoEditingField(null);
                            }
                          }}
                          className="h-6 text-right"
                          autoFocus
                        />
                      ) : (
                        formatCurrency(item.consuntivoValue || 0)
                      )}
                    </TableCell>
                    {/* Consuntivo % Column */}
                    <TableCell className={`text-center ${item.highlight ? "font-medium" : ""}`}>
                      {item.isFromDashboard && item.foodCostPercent !== null ? 
                        formatPercent(item.foodCostPercent) : 
                        formatPercent(item.consuntivoPercent * 100)
                      }
                    </TableCell>
                  </TableRow>
                ))}

                {/* EBITDA Row */}
                <TableRow className="bg-green-50 dark:bg-green-950/20 border-t-2 font-bold">
                  <TableCell className="font-bold">10 - EBITDA</TableCell>
                  <TableCell className="text-center font-bold">{formatPercent(ebitdaPercent * 100)}</TableCell>
                  <TableCell className="text-right font-bold" data-testid="eco-ebitda-budget">
                    {formatCurrency(ebitdaBudgetEuros)}
                  </TableCell>
                  <TableCell className="text-right font-bold" data-testid="eco-ebitda-consuntivo">
                    {formatCurrency(ebitdaConsuntivoEuros)}
                  </TableCell>
                  <TableCell className="text-center font-bold">
                    <Badge 
                      variant="secondary"
                      className={`font-mono text-xs ${getPercentageColor(ebitdaPercentConsuntivo * 100)}`}
                    >
                      {formatPercent(ebitdaPercentConsuntivo * 100)}
                    </Badge>
                  </TableCell>
                </TableRow>
              </TableBody>
            </Table>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/auth-page.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { useAuth } from "@/hooks/use-auth";
import { Eye, EyeOff, ChefHat, Utensils } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

const loginSchema = z.object({
  username: z.string().min(1, "Username è richiesto"),
  password: z.string().min(1, "Password è richiesta"),
});

const registerSchema = z.object({
  username: z.string().min(3, "Username deve essere almeno 3 caratteri"),
  email: z.string().email("Email non valida"),
  password: z.string().min(6, "Password deve essere almeno 6 caratteri"),
  confirmPassword: z.string().min(1, "Conferma password è richiesta"),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Le password non coincidono",
  path: ["confirmPassword"],
});

type LoginFormData = z.infer<typeof loginSchema>;
type RegisterFormData = z.infer<typeof registerSchema>;

export default function AuthPage() {
  const [mode, setMode] = useState<"login" | "register">("login");
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const { login, register } = useAuth();
  const { toast } = useToast();

  const loginForm = useForm<LoginFormData>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      username: "",
      password: "",
    },
  });

  const registerForm = useForm<RegisterFormData>({
    resolver: zodResolver(registerSchema),
    defaultValues: {
      username: "",
      email: "",
      password: "",
      confirmPassword: "",
    },
  });

  const onLogin = async (data: LoginFormData) => {
    try {
      setError(null);
      await login(data.username, data.password);
      toast({
        title: "Accesso effettuato!",
        description: "Benvenuto in FoodyFlow",
      });
    } catch (err) {
      setError(err instanceof Error ? err.message : "Errore durante l'accesso");
    }
  };

  const onRegister = async (data: RegisterFormData) => {
    try {
      setError(null);
      await register(data.username, data.email, data.password);
      toast({
        title: "Registrazione completata!",
        description: "Il tuo account è stato creato con successo",
      });
    } catch (err) {
      setError(err instanceof Error ? err.message : "Errore durante la registrazione");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-background via-background to-muted/20 flex items-center justify-center p-4">
      {/* Background decorative elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-1/4 left-1/4 w-32 h-32 bg-primary/5 rounded-full blur-3xl"></div>
        <div className="absolute bottom-1/4 right-1/4 w-48 h-48 bg-secondary/5 rounded-full blur-3xl"></div>
        <div className="absolute top-1/2 right-1/3 w-24 h-24 bg-accent/5 rounded-full blur-2xl"></div>
      </div>

      <Card className="w-full max-w-md shadow-2xl border-border/50 relative z-10" data-testid="card-auth">
        <CardHeader className="text-center space-y-4 pb-8">
          {/* FoodyFlow Logo & Brand */}
          <div className="flex items-center justify-center space-x-3 mb-2">
            <div className="relative">
              <ChefHat className="h-8 w-8 text-primary" />
              <Utensils className="h-4 w-4 text-secondary absolute -bottom-1 -right-1" />
            </div>
            <div>
              <h1 className="text-2xl font-bold text-foreground tracking-tight" data-testid="text-brand-title">
                FoodyFlow
              </h1>
              <p className="text-sm text-muted-foreground italic tracking-wide" data-testid="text-brand-payoff">
                Evolve Your Eatery
              </p>
            </div>
          </div>

          <div>
            <CardTitle className="text-xl text-foreground" data-testid="text-auth-title">
              {mode === "login" ? "Accedi al tuo account" : "Crea nuovo account"}
            </CardTitle>
            <CardDescription className="text-muted-foreground mt-2" data-testid="text-auth-description">
              {mode === "login" 
                ? "Gestisci il tuo ristorante con efficienza professionale" 
                : "Inizia la tua evoluzione culinaria oggi"}
            </CardDescription>
          </div>
        </CardHeader>

        <CardContent className="space-y-6">
          {error && (
            <Alert variant="destructive" data-testid="alert-error">
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}

          {mode === "login" ? (
            <Form {...loginForm}>
              <form onSubmit={loginForm.handleSubmit(onLogin)} className="space-y-4" data-testid="form-login">
                <FormField
                  control={loginForm.control}
                  name="username"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Username</FormLabel>
                      <FormControl>
                        <Input 
                          placeholder="Inserisci il tuo username" 
                          data-testid="input-username"
                          {...field} 
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={loginForm.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Password</FormLabel>
                      <FormControl>
                        <div className="relative">
                          <Input
                            type={showPassword ? "text" : "password"}
                            placeholder="Inserisci la tua password"
                            data-testid="input-password"
                            {...field}
                          />
                          <Button
                            type="button"
                            variant="ghost"
                            size="icon"
                            className="absolute right-0 top-0 h-full px-3 hover:bg-transparent"
                            onClick={() => setShowPassword(!showPassword)}
                            data-testid="button-toggle-password"
                          >
                            {showPassword ? (
                              <EyeOff className="h-4 w-4 text-muted-foreground" />
                            ) : (
                              <Eye className="h-4 w-4 text-muted-foreground" />
                            )}
                          </Button>
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Button 
                  type="submit" 
                  className="w-full bg-primary hover:bg-primary/90" 
                  disabled={loginForm.formState.isSubmitting}
                  data-testid="button-login"
                >
                  {loginForm.formState.isSubmitting ? "Accesso in corso..." : "Accedi"}
                </Button>
              </form>
            </Form>
          ) : (
            <Form {...registerForm}>
              <form onSubmit={registerForm.handleSubmit(onRegister)} className="space-y-4" data-testid="form-register">
                <FormField
                  control={registerForm.control}
                  name="username"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Username</FormLabel>
                      <FormControl>
                        <Input 
                          placeholder="Scegli un username"
                          data-testid="input-register-username"
                          {...field} 
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={registerForm.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Email</FormLabel>
                      <FormControl>
                        <Input 
                          type="email" 
                          placeholder="la-tua-email@esempio.com"
                          data-testid="input-email"
                          {...field} 
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={registerForm.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Password</FormLabel>
                      <FormControl>
                        <div className="relative">
                          <Input
                            type={showPassword ? "text" : "password"}
                            placeholder="Crea una password sicura"
                            data-testid="input-register-password"
                            {...field}
                          />
                          <Button
                            type="button"
                            variant="ghost"
                            size="icon"
                            className="absolute right-0 top-0 h-full px-3 hover:bg-transparent"
                            onClick={() => setShowPassword(!showPassword)}
                            data-testid="button-toggle-register-password"
                          >
                            {showPassword ? (
                              <EyeOff className="h-4 w-4 text-muted-foreground" />
                            ) : (
                              <Eye className="h-4 w-4 text-muted-foreground" />
                            )}
                          </Button>
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={registerForm.control}
                  name="confirmPassword"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Conferma Password</FormLabel>
                      <FormControl>
                        <div className="relative">
                          <Input
                            type={showConfirmPassword ? "text" : "password"}
                            placeholder="Conferma la tua password"
                            data-testid="input-confirm-password"
                            {...field}
                          />
                          <Button
                            type="button"
                            variant="ghost"
                            size="icon"
                            className="absolute right-0 top-0 h-full px-3 hover:bg-transparent"
                            onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                            data-testid="button-toggle-confirm-password"
                          >
                            {showConfirmPassword ? (
                              <EyeOff className="h-4 w-4 text-muted-foreground" />
                            ) : (
                              <Eye className="h-4 w-4 text-muted-foreground" />
                            )}
                          </Button>
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Button 
                  type="submit" 
                  className="w-full bg-primary hover:bg-primary/90" 
                  disabled={registerForm.formState.isSubmitting}
                  data-testid="button-register"
                >
                  {registerForm.formState.isSubmitting ? "Registrazione in corso..." : "Crea Account"}
                </Button>
              </form>
            </Form>
          )}

          {/* Switch between login/register */}
          <div className="text-center pt-4 border-t border-border/50">
            <p className="text-sm text-muted-foreground">
              {mode === "login" ? "Non hai ancora un account?" : "Hai già un account?"}
            </p>
            <Button
              type="button"
              variant="ghost"
              onClick={() => {
                setMode(mode === "login" ? "register" : "login");
                setError(null);
                loginForm.reset();
                registerForm.reset();
              }}
              className="text-primary hover:text-primary/80 p-0 h-auto font-medium"
              data-testid={mode === "login" ? "link-register" : "link-login"}
            >
              {mode === "login" ? "Registrati ora" : "Accedi invece"}
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/Recipes.tsx.content:
import { useState, useEffect, useCallback } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from "@/components/ui/collapsible";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { ChefHat, Utensils, Plus, Calculator, ChevronDown, AlertTriangle } from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import RecipeForm from "@/components/RecipeForm";
import RecipeList from "@/components/RecipeList";
import DishForm from "@/components/DishForm";
import DishList from "@/components/DishList";
import {
  useRecipes,
  useDishes,
  useProducts,
  useCreateRecipe,
  useUpdateRecipe,
  useDeleteRecipe,
  useCreateDish,
  useUpdateDish,
  useDeleteDish,
} from "@/hooks/useApi";
import type { Recipe, Dish, Product, InsertRecipe, InsertDish } from "@shared/schema";

export default function Recipes() {
  const [activeTab, setActiveTab] = useState("recipes");
  const [editingRecipe, setEditingRecipe] = useState<Recipe | undefined>();
  const [editingDish, setEditingDish] = useState<Dish | undefined>();

  // Recipe calculator state
  const [selectedRecipeId, setSelectedRecipeId] = useState<string>("");
  const [targetKg, setTargetKg] = useState<string>("");
  const [calculatedIngredients, setCalculatedIngredients] = useState<{product: Product, quantity: number, finishedQuantity: number, cost: number, hasWeightAdjustment: boolean}[]>([]);
  const [isCalculatorOpen, setIsCalculatorOpen] = useState(false);
  const [calculationError, setCalculationError] = useState<string>("");

  // Data fetching
  const { data: products = [] } = useProducts();
  const { data: recipes = [] } = useRecipes();
  const { data: dishes = [] } = useDishes();

  // Mutations
  const createRecipeMutation = useCreateRecipe();
  const updateRecipeMutation = useUpdateRecipe();
  const deleteRecipeMutation = useDeleteRecipe();

  const createDishMutation = useCreateDish();
  const updateDishMutation = useUpdateDish();
  const deleteDishMutation = useDeleteDish();

  // Recipe handlers
  const handleCreateRecipe = (data: InsertRecipe) => {
    createRecipeMutation.mutate(data);
  };

  const handleUpdateRecipe = (id: string, data: InsertRecipe) => {
    updateRecipeMutation.mutate({ id, data });
    setEditingRecipe(undefined);
  };

  const handleDeleteRecipe = (id: string) => {
    deleteRecipeMutation.mutate(id);
  };

  // Dish handlers
  const handleCreateDish = (data: InsertDish) => {
    createDishMutation.mutate(data);
  };

  const handleUpdateDish = (id: string, data: InsertDish) => {
    updateDishMutation.mutate({ id, data });
    setEditingDish(undefined);
  };

  const handleDeleteDish = (id: string) => {
    deleteDishMutation.mutate(id);
  };

  // Recipe calculator handlers with improved validation
  const calculateIngredients = useCallback(() => {
    setCalculationError("");

    if (!selectedRecipeId || !targetKg || parseFloat(targetKg) <= 0) {
      setCalculatedIngredients([]);
      return;
    }

    const recipe = recipes.find(r => r.id === selectedRecipeId);
    if (!recipe) {
      setCalculatedIngredients([]);
      return;
    }

    const multiplier = parseFloat(targetKg);
    const weightAdjustment = recipe.weightAdjustment || 0;

    // Guard against division by zero or invalid weight adjustments
    const weightAdjustmentFactor = 1 + weightAdjustment / 100;

    if (weightAdjustmentFactor <= 0) {
      setCalculationError(`Peso adjustment di ${weightAdjustment}% non valido. Non può essere -100% o inferiore.`);
      setCalculatedIngredients([]);
      return;
    }

    const adjustedMultiplier = multiplier / weightAdjustmentFactor;

    // Check if adjustedMultiplier is finite
    if (!isFinite(adjustedMultiplier)) {
      setCalculationError("Errore di calcolo: moltiplicatore non valido.");
      setCalculatedIngredients([]);
      return;
    }

    const ingredients = recipe.ingredients.map(ingredient => {
      const product = products.find(p => p.id === ingredient.productId);
      if (!product) return null;

      // Calculate raw quantity needed (what we actually buy/use)
      const rawQuantity = ingredient.quantity * adjustedMultiplier;
      const cost = ingredient.cost * adjustedMultiplier;

      // Validate calculated values
      if (!isFinite(rawQuantity) || !isFinite(cost)) {
        return null;
      }

      return {
        product,
        quantity: rawQuantity, // This is the actual quantity to buy
        finishedQuantity: ingredient.quantity * multiplier, // This is the final result
        cost: cost,
        hasWeightAdjustment: weightAdjustment !== 0
      };
    }).filter(Boolean) as {product: Product, quantity: number, finishedQuantity: number, cost: number, hasWeightAdjustment: boolean}[];

    // Final validation: check if all calculations are valid
    if (ingredients.length === 0 && recipe.ingredients.length > 0) {
      setCalculationError("Errore nel calcolo degli ingredienti.");
      return;
    }

    setCalculatedIngredients(ingredients);
  }, [selectedRecipeId, targetKg, recipes, products]);

  // Auto-recalculate when dependencies change
  useEffect(() => {
    if (selectedRecipeId && targetKg) {
      calculateIngredients();
    } else {
      setCalculatedIngredients([]);
      setCalculationError("");
    }
  }, [calculateIngredients, selectedRecipeId, targetKg]);

  const getTotalCost = () => {
    return calculatedIngredients.reduce((sum, ing) => sum + ing.cost, 0);
  };

  const getCalculationSummary = () => {
    if (calculatedIngredients.length === 0) return "Seleziona ricetta e quantità";

    const totalCost = getTotalCost();
    const ingredientCount = calculatedIngredients.length;
    const hasWeightAdjustment = calculatedIngredients.some(ing => ing.hasWeightAdjustment);
    const recipe = recipes.find(r => r.id === selectedRecipeId);
    const weightInfo = hasWeightAdjustment && recipe ? ` (peso ${recipe.weightAdjustment > 0 ? '+' : ''}${recipe.weightAdjustment}%)` : '';

    return `${targetKg} kg${weightInfo} • ${ingredientCount} ingredienti • €${totalCost.toFixed(2)}`;
  };


  return (
    <div className="container mx-auto p-6 space-y-6">
      {/* Header */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <ChefHat className="h-6 w-6 text-primary" />
              <CardTitle className="text-2xl">Gestione Ricette e Piatti</CardTitle>
            </div>
          </div>
        </CardHeader>
      </Card>

      {/* Main Content */}
      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="recipes" className="flex items-center gap-2">
            <ChefHat className="h-4 w-4" />
            Ricette
          </TabsTrigger>
          <TabsTrigger value="dishes" className="flex items-center gap-2">
            <Utensils className="h-4 w-4" />
            Piatti
          </TabsTrigger>
        </TabsList>

        <TabsContent value="recipes" className="space-y-6">
          <div className="grid gap-6 lg:grid-cols-3">
            {/* Recipe Form */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Plus className="h-5 w-5" />
                  {editingRecipe ? "Modifica Ricetta" : "Nuova Ricetta"}
                </CardTitle>
              </CardHeader>
              <CardContent>
                <RecipeForm
                  products={products}
                  editRecipe={editingRecipe}
                  onSubmit={editingRecipe 
                    ? (data) => handleUpdateRecipe(editingRecipe.id, data)
                    : handleCreateRecipe
                  }
                  onCancel={() => setEditingRecipe(undefined)}
                  data-testid="recipe-form"
                />
              </CardContent>
            </Card>

            {/* Recipe Calculator */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Calculator className="h-5 w-5" />
                  Calcolatrice Ricette
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="recipe-select">Ricetta</Label>
                  <Select value={selectedRecipeId} onValueChange={setSelectedRecipeId}>
                    <SelectTrigger data-testid="select-recipe">
                      <SelectValue placeholder="Seleziona una ricetta" />
                    </SelectTrigger>
                    <SelectContent>
                      {recipes.map((recipe) => (
                        <SelectItem key={recipe.id} value={recipe.id}>
                          {recipe.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="target-kg">Kg di semilavorato</Label>
                  <Input
                    id="target-kg"
                    type="number"
                    min="0"
                    step="0.1"
                    value={targetKg}
                    onChange={(e) => setTargetKg(e.target.value)}
                    placeholder="es. 3"
                    data-testid="input-target-kg"
                  />
                </div>

                {calculationError && (
                  <Alert variant="destructive">
                    <AlertTriangle className="h-4 w-4" />
                    <AlertDescription>{calculationError}</AlertDescription>
                  </Alert>
                )}

                <Button 
                  onClick={calculateIngredients} 
                  className="w-full"
                  disabled={!selectedRecipeId || !targetKg || parseFloat(targetKg) <= 0}
                  data-testid="button-calculate"
                >
                  <Calculator className="w-4 h-4 mr-2" />
                  Calcola Ingredienti
                </Button>

                {calculatedIngredients.length > 0 && (
                  <Collapsible open={isCalculatorOpen} onOpenChange={setIsCalculatorOpen}>
                    <CollapsibleTrigger asChild>
                      <Button 
                        variant="outline" 
                        className="w-full justify-between"
                        data-testid="button-toggle-ingredients"
                      >
                        <span>{getCalculationSummary()}</span>
                        <ChevronDown className={`h-4 w-4 transition-transform ${isCalculatorOpen ? 'rotate-180' : ''}`} />
                      </Button>
                    </CollapsibleTrigger>
                    <CollapsibleContent className="space-y-2 mt-2">
                      <div className="bg-muted p-3 rounded-md space-y-2">
                        <h4 className="font-semibold text-sm">Ingredienti necessari:</h4>
                        {calculatedIngredients.map((ingredient, index) => {
                          const recipe = recipes.find(r => r.id === selectedRecipeId);
                          const hasWeightAdjustment = ingredient.hasWeightAdjustment;

                          return (
                            <div key={index} className="space-y-1">
                              <div className="flex justify-between text-sm">
                                <span className="font-medium">{ingredient.product.name}</span>
                                <span className="font-mono">€{ingredient.cost.toFixed(2)}</span>
                              </div>
                              {hasWeightAdjustment ? (
                                <div className="text-xs text-muted-foreground ml-2 space-y-0.5">
                                  <div>
                                    • Da comprare (crudo): <span className="font-mono">{ingredient.quantity.toFixed(2)} {ingredient.product.unit}</span>
                                  </div>
                                  <div>
                                    • Risultato (cotto): <span className="font-mono">{ingredient.finishedQuantity.toFixed(2)} {ingredient.product.unit}</span>
                                  </div>
                                  <div>
                                    • Peso: {recipe?.weightAdjustment && recipe.weightAdjustment > 0 ? '+' : ''}{recipe?.weightAdjustment || 0}% durante la lavorazione
                                  </div>
                                </div>
                              ) : (
                                <div className="text-xs text-muted-foreground ml-2">
                                  • Quantità: <span className="font-mono">{ingredient.quantity.toFixed(2)} {ingredient.product.unit}</span>
                                </div>
                              )}
                            </div>
                          );
                        })}
                        <hr className="my-2" />
                        <div className="flex justify-between font-semibold text-sm">
                          <span>Totale</span>
                          <span className="font-mono">€{getTotalCost().toFixed(2)}</span>
                        </div>
                      </div>
                    </CollapsibleContent>
                  </Collapsible>
                )}
              </CardContent>
            </Card>
          </div>

          <div className="space-y-6">
            {/* Recipe List */}
            <RecipeList
              recipes={recipes}
              products={products}
              onEdit={setEditingRecipe}
              onDelete={handleDeleteRecipe}
              data-testid="recipe-list"
            />
          </div>
        </TabsContent>

        <TabsContent value="dishes" className="space-y-6">
          <div className="grid gap-6 lg:grid-cols-2">
            {/* Dish Form */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Plus className="h-5 w-5" />
                  {editingDish ? "Modifica Piatto" : "Nuovo Piatto"}
                </CardTitle>
              </CardHeader>
              <CardContent>
                <DishForm
                  products={products}
                  recipes={recipes}
                  editDish={editingDish}
                  onSubmit={editingDish
                    ? (data) => handleUpdateDish(editingDish.id, data)
                    : handleCreateDish
                  }
                  onCancel={() => setEditingDish(undefined)}
                  data-testid="dish-form"
                />
              </CardContent>
            </Card>

            {/* Dish List */}
            <div>
              <DishList
                dishes={dishes}
                products={products}
                onEdit={setEditingDish}
                onDelete={handleDeleteDish}
                data-testid="dish-list"
              />
            </div>
          </div>
        </TabsContent>
      </Tabs>
    </div>
  );
}

