import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from "@/components/ui/alert-dialog";
import { Plus, Edit, Trash2, Users } from "lucide-react";
import { useSuppliers, useCreateSupplier, useUpdateSupplier, useDeleteSupplier } from "@/hooks/useApi";
import { insertSupplierSchema, updateSupplierSchema } from "@shared/schema";
import type { Supplier, InsertSupplier, UpdateSupplier } from "@shared/schema";
import { z } from "zod";

const formSchema = insertSupplierSchema.extend({
  email: z.string().email("Formato email non valido").optional().or(z.literal("")),
});

export default function Suppliers() {
  const [showForm, setShowForm] = useState(false);
  const [editingSupplier, setEditingSupplier] = useState<Supplier | undefined>();

  const { data: suppliers, isLoading } = useSuppliers();
  const createSupplier = useCreateSupplier();
  const updateSupplier = useUpdateSupplier();
  const deleteSupplier = useDeleteSupplier();

  const form = useForm<z.infer<typeof formSchema>>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      name: "",
      email: "",
      notes: "",
    },
  });

  const onSubmit = async (values: z.infer<typeof formSchema>) => {
    try {
      if (editingSupplier) {
        // Update existing supplier
        const updateData: UpdateSupplier = {
          name: values.name,
          email: values.email || undefined,
          notes: values.notes || undefined,
        };
        await updateSupplier.mutateAsync({ id: editingSupplier.id, data: updateData });
      } else {
        // Create new supplier
        const supplierData: InsertSupplier = {
          name: values.name,
          email: values.email || undefined,
          notes: values.notes || undefined,
        };
        await createSupplier.mutateAsync(supplierData);
      }
      resetForm();
    } catch (error) {
      console.error("Error saving supplier:", error);
    }
  };

  const handleEdit = (supplier: Supplier) => {
    setEditingSupplier(supplier);
    form.setValue("name", supplier.name);
    form.setValue("email", supplier.email || "");
    form.setValue("notes", supplier.notes || "");
    setShowForm(true);
  };

  const handleDelete = async (id: string) => {
    try {
      await deleteSupplier.mutateAsync(id);
    } catch (error) {
      console.error("Error deleting supplier:", error);
    }
  };

  const resetForm = () => {
    form.reset();
    setEditingSupplier(undefined);
    setShowForm(false);
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-48">
        <div className="text-muted-foreground">Caricamento fornitori...</div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-3">
          <Users className="h-8 w-8 text-primary" />
          <div>
            <h1 className="text-3xl font-bold tracking-tight">Fornitori</h1>
            <p className="text-muted-foreground">
              Gestisci i fornitori dei tuoi prodotti
            </p>
          </div>
        </div>
        <Button 
          onClick={() => setShowForm(true)} 
          data-testid="button-add-supplier"
          className="gap-2"
        >
          <Plus className="h-4 w-4" />
          Nuovo Fornitore
        </Button>
      </div>

      {/* Form */}
      {showForm && (
        <Card>
          <CardHeader>
            <CardTitle>
              {editingSupplier ? "Modifica Fornitore" : "Nuovo Fornitore"}
            </CardTitle>
            <CardDescription>
              {editingSupplier ? "Modifica i dati del fornitore" : "Inserisci i dati del nuovo fornitore"}
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="name"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Nome Fornitore *</FormLabel>
                      <FormControl>
                        <Input
                          placeholder="Inserisci il nome del fornitore"
                          data-testid="input-supplier-name"
                          {...field}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Email</FormLabel>
                      <FormControl>
                        <Input
                          type="email"
                          placeholder="fornitore@esempio.com"
                          data-testid="input-supplier-email"
                          {...field}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="notes"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Note</FormLabel>
                      <FormControl>
                        <Textarea
                          placeholder="Note aggiuntive sul fornitore (opzionale)"
                          data-testid="input-supplier-notes"
                          {...field}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="flex gap-2 pt-4">
                  <Button 
                    type="submit"
                    data-testid="button-save-supplier"
                    disabled={createSupplier.isPending || updateSupplier.isPending}
                  >
                    {(createSupplier.isPending || updateSupplier.isPending) ? "Salvando..." : editingSupplier ? "Aggiorna" : "Salva"}
                  </Button>
                  <Button
                    type="button"
                    variant="outline"
                    onClick={resetForm}
                    data-testid="button-cancel-supplier"
                  >
                    Annulla
                  </Button>
                </div>
              </form>
            </Form>
          </CardContent>
        </Card>
      )}

      {/* Suppliers List */}
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        {suppliers?.map((supplier) => (
          <Card key={supplier.id} className="hover-elevate">
            <CardHeader className="pb-3">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <CardTitle className="text-lg" data-testid={`text-supplier-name-${supplier.id}`}>
                    {supplier.name}
                  </CardTitle>
                  {supplier.email && (
                    <p className="text-sm text-muted-foreground mt-1" data-testid={`text-supplier-email-${supplier.id}`}>
                      {supplier.email}
                    </p>
                  )}
                </div>
                <div className="flex gap-1">
                  <Button
                    size="icon"
                    variant="ghost"
                    onClick={() => handleEdit(supplier)}
                    data-testid={`button-edit-supplier-${supplier.id}`}
                  >
                    <Edit className="h-4 w-4" />
                  </Button>
                  <AlertDialog>
                    <AlertDialogTrigger asChild>
                      <Button
                        size="icon"
                        variant="ghost"
                        data-testid={`button-delete-supplier-${supplier.id}`}
                      >
                        <Trash2 className="h-4 w-4 text-destructive" />
                      </Button>
                    </AlertDialogTrigger>
                    <AlertDialogContent>
                      <AlertDialogHeader>
                        <AlertDialogTitle>Conferma eliminazione</AlertDialogTitle>
                        <AlertDialogDescription>
                          Sei sicuro di voler eliminare il fornitore "{supplier.name}"? 
                          Questa azione non può essere annullata.
                        </AlertDialogDescription>
                      </AlertDialogHeader>
                      <AlertDialogFooter>
                        <AlertDialogCancel>Annulla</AlertDialogCancel>
                        <AlertDialogAction
                          onClick={() => handleDelete(supplier.id)}
                          className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                          data-testid={`button-confirm-delete-supplier-${supplier.id}`}
                        >
                          Elimina
                        </AlertDialogAction>
                      </AlertDialogFooter>
                    </AlertDialogContent>
                  </AlertDialog>
                </div>
              </div>
            </CardHeader>
            {supplier.notes && (
              <CardContent className="pt-0">
                <p className="text-sm text-muted-foreground" data-testid={`text-supplier-notes-${supplier.id}`}>
                  {supplier.notes}
                </p>
              </CardContent>
            )}
          </Card>
        ))}
      </div>

      {suppliers?.length === 0 && (
        <Card>
          <CardContent className="flex flex-col items-center justify-center py-12">
            <Users className="h-12 w-12 text-muted-foreground mb-4" />
            <h3 className="text-lg font-medium mb-2">Nessun fornitore presente</h3>
            <p className="text-muted-foreground text-center mb-4">
              Inizia aggiungendo il primo fornitore per organizzare meglio i tuoi prodotti
            </p>
            <Button onClick={() => setShowForm(true)} data-testid="button-add-first-supplier">
              <Plus className="h-4 w-4 mr-2" />
              Aggiungi il primo fornitore
            </Button>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/AIAssistant.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Bot, Send, Sparkles, TrendingUp, ChefHat } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface AIResponse {
  success: boolean;
  analysis?: string;
  optimization?: {
    currentPercentage: number;
    targetPercentage: number;
    suggestions: string[];
    priority: 'high' | 'medium' | 'low';
  };
  suggestions?: string;
  timestamp: string;
}

export function AIAssistant() {
  const [query, setQuery] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [response, setResponse] = useState<string>("");
  const [optimization, setOptimization] = useState<any>(null);
  const [menuSuggestions, setMenuSuggestions] = useState<string>("");
  const [marketTrends, setMarketTrends] = useState("");
  const { toast } = useToast();

  const handleAnalyze = async () => {
    if (!query.trim()) {
      toast({
        title: "Domanda richiesta",
        description: "Inserisci una domanda per l'assistente IA",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);
    try {
      const result = await apiRequest("/api/ai/analyze", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ query }),
      });

      if (result.success && result.analysis) {
        setResponse(result.analysis);
        toast({
          title: "Analisi completata",
          description: "L'assistente IA ha analizzato i tuoi dati",
        });
      }
    } catch (error) {
      toast({
        title: "Errore",
        description: "Errore nell'analisi IA. Riprova più tardi.",
        variant: "destructive",
      });
      console.error("Errore analisi IA:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleFoodCostOptimization = async () => {
    setIsLoading(true);
    try {
      const result = await apiRequest("/api/ai/food-cost-optimization", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({}),
      });

      if (result.success && result.optimization) {
        setOptimization(result.optimization);
        toast({
          title: "Ottimizzazione completata",
          description: "Analisi food cost generata con successo",
        });
      }
    } catch (error) {
      toast({
        title: "Errore",
        description: "Errore nell'ottimizzazione food cost. Riprova più tardi.",
        variant: "destructive",
      });
      console.error("Errore ottimizzazione:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleMenuSuggestions = async () => {
    setIsLoading(true);
    try {
      const result = await apiRequest("/api/ai/menu-suggestions", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ marketTrends }),
      });

      if (result.success && result.suggestions) {
        setMenuSuggestions(result.suggestions);
        toast({
          title: "Suggerimenti generati",
          description: "Suggerimenti per il menu creati con successo",
        });
      }
    } catch (error) {
      toast({
        title: "Errore",
        description: "Errore nella generazione suggerimenti. Riprova più tardi.",
        variant: "destructive",
      });
      console.error("Errore suggerimenti:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high': return 'bg-red-100 text-red-800 border-red-200';
      case 'medium': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'low': return 'bg-green-100 text-green-800 border-green-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Bot className="h-5 w-5 text-primary" />
            Assistente IA FoodyFlow
          </CardTitle>
          <CardDescription>
            Analizza i tuoi dati con l'intelligenza artificiale per ottimizzare il ristorante
          </CardDescription>
        </CardHeader>
      </Card>

      <Tabs defaultValue="analyze" className="w-full">
        <TabsList className="grid w-full grid-cols-3">
          <TabsTrigger value="analyze" className="flex items-center gap-2">
            <Sparkles className="h-4 w-4" />
            Analisi Generale
          </TabsTrigger>
          <TabsTrigger value="foodcost" className="flex items-center gap-2">
            <TrendingUp className="h-4 w-4" />
            Food Cost
          </TabsTrigger>
          <TabsTrigger value="menu" className="flex items-center gap-2">
            <ChefHat className="h-4 w-4" />
            Menu Engineering
          </TabsTrigger>
        </TabsList>

        <TabsContent value="analyze" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Analisi Personalizzata</CardTitle>
              <CardDescription>
                Fai una domanda sui tuoi dati e ricevi consigli personalizzati
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="flex gap-2">
                <Input
                  placeholder="Es: Come posso ridurre il food cost? Quali piatti sono più redditizi?"
                  value={query}
                  onChange={(e) => setQuery(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && handleAnalyze()}
                  data-testid="input-ai-query"
                />
                <Button 
                  onClick={handleAnalyze} 
                  disabled={isLoading || !query.trim()}
                  data-testid="button-analyze-ai"
                >
                  {isLoading ? "Analizzando..." : <Send className="h-4 w-4" />}
                </Button>
              </div>

              {response && (
                <Card className="bg-muted/50">
                  <CardContent className="p-4">
                    <div className="whitespace-pre-wrap text-sm" data-testid="text-ai-response">
                      {response}
                    </div>
                  </CardContent>
                </Card>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="foodcost" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Ottimizzazione Food Cost</CardTitle>
              <CardDescription>
                Analisi automatica dei costi alimentari e suggerimenti per l'ottimizzazione
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <Button 
                onClick={handleFoodCostOptimization} 
                disabled={isLoading}
                className="w-full"
                data-testid="button-optimize-foodcost"
              >
                {isLoading ? "Analizzando..." : "Analizza Food Cost"}
              </Button>

              {optimization && (
                <div className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <Card>
                      <CardContent className="p-4 text-center">
                        <div className="text-2xl font-bold text-red-600">
                          {optimization.currentPercentage}%
                        </div>
                        <div className="text-sm text-muted-foreground">Food Cost Attuale</div>
                      </CardContent>
                    </Card>
                    <Card>
                      <CardContent className="p-4 text-center">
                        <div className="text-2xl font-bold text-green-600">
                          {optimization.targetPercentage}%
                        </div>
                        <div className="text-sm text-muted-foreground">Obiettivo Consigliato</div>
                      </CardContent>
                    </Card>
                  </div>

                  <div className="flex items-center gap-2 mb-2">
                    <Badge className={getPriorityColor(optimization.priority)}>
                      Priorità {optimization.priority}
                    </Badge>
                  </div>

                  <Card>
                    <CardHeader>
                      <CardTitle className="text-lg">Suggerimenti per l'Ottimizzazione</CardTitle>
                    </CardHeader>
                    <CardContent>
                      <ul className="space-y-2">
                        {optimization.suggestions.map((suggestion: string, index: number) => (
                          <li key={index} className="flex items-start gap-2">
                            <span className="text-primary mt-1">•</span>
                            <span className="text-sm">{suggestion}</span>
                          </li>
                        ))}
                      </ul>
                    </CardContent>
                  </Card>
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="menu" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Suggerimenti Menu</CardTitle>
              <CardDescription>
                Ottimizza il tuo menu basandoti sui dati dei piatti e sui trend di mercato
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <Textarea
                placeholder="Inserisci trend di mercato o preferenze specifiche (opzionale)"
                value={marketTrends}
                onChange={(e) => setMarketTrends(e.target.value)}
                rows={3}
                data-testid="textarea-market-trends"
              />

              <Button 
                onClick={handleMenuSuggestions} 
                disabled={isLoading}
                className="w-full"
                data-testid="button-menu-suggestions"
              >
                {isLoading ? "Generando suggerimenti..." : "Genera Suggerimenti Menu"}
              </Button>

              {menuSuggestions && (
                <Card className="bg-muted/50">
                  <CardContent className="p-4">
                    <div className="whitespace-pre-wrap text-sm" data-testid="text-menu-suggestions">
                      {menuSuggestions}
                    </div>
                  </CardContent>
                </Card>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/components/AppSidebar.tsx.content:
import { 
  LayoutDashboard, 
  Calendar, 
  Calculator, 
  Users, 
  BarChart3, 
  Warehouse, 
  ChefHat, 
  Truck, 
  ArrowUpDown, 
  Trash2,
  Utensils,
  TrendingUp,
  Bot
} from "lucide-react";
import { useLocation } from "wouter";
import { 
  Sidebar, 
  SidebarContent, 
  SidebarGroup, 
  SidebarGroupContent, 
  SidebarGroupLabel, 
  SidebarMenu, 
  SidebarMenuButton, 
  SidebarMenuItem,
  SidebarHeader
} from "@/components/ui/sidebar";

// Primary operational tabs
const primaryTabs = [
  { id: "dashboard", label: "Dashboard", icon: LayoutDashboard },
  { id: "budget", label: "Budget", icon: Calendar },
  { id: "profit-loss", label: "P&L", icon: TrendingUp },
  { id: "food-cost", label: "Food Cost", icon: Calculator },
  { id: "labour-cost", label: "Labour Cost", icon: Users },
  { id: "sales-detail", label: "Vendite", icon: BarChart3 },
];

// Management section tabs
const managementTabs = [
  { id: "suppliers", label: "Fornitori", icon: Users },
  { id: "inventory", label: "Inventario", icon: Warehouse },
  { id: "recipes", label: "Ricette", icon: ChefHat },
  { id: "orders", label: "Ordini", icon: Truck },
  { id: "waste", label: "Sprechi/Staff Food", icon: Trash2 },
  { id: "warehouse", label: "Magazzino", icon: ArrowUpDown },
];

interface AppSidebarProps {
  activeTab: string;
  onTabChange: (tab: string) => void;
}

export function AppSidebar({ activeTab, onTabChange }: AppSidebarProps) {
  const [location, navigate] = useLocation();

  const handleNavigation = (item: any) => {
    // Ensure we're on the main route and just change the tab
    navigate("/");
    onTabChange(item.id);
  };

  const isActive = (item: any) => {
    return activeTab === item.id;
  };

  return (
    <Sidebar>
      <SidebarHeader className="border-b border-border">
        <div className="flex items-center gap-2 px-4 py-3">
          <Utensils className="h-6 w-6 text-primary" />
          <div className="flex flex-col">
            <h1 className="text-lg font-bold text-foreground">FoodyFlow</h1>
            <p className="text-xs text-muted-foreground italic">Evolve Your Eatery</p>
          </div>
        </div>
      </SidebarHeader>
      <SidebarContent>
        <SidebarGroup>
          <SidebarGroupLabel>Operazioni</SidebarGroupLabel>
          <SidebarGroupContent>
            <SidebarMenu>
              {primaryTabs.map((item) => {
                const Icon = item.icon;
                return (
                  <SidebarMenuItem key={item.id}>
                    <SidebarMenuButton 
                      onClick={() => handleNavigation(item)}
                      isActive={isActive(item)}
                      data-testid={`sidebar-${item.id}`}
                    >
                      <Icon className="h-4 w-4" />
                      <span>{item.label}</span>
                    </SidebarMenuButton>
                  </SidebarMenuItem>
                );
              })}
            </SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>

        <SidebarGroup>
          <SidebarGroupLabel>Gestione</SidebarGroupLabel>
          <SidebarGroupContent>
            <SidebarMenu>
              {managementTabs.map((item) => {
                const Icon = item.icon;
                return (
                  <SidebarMenuItem key={item.id}>
                    <SidebarMenuButton 
                      onClick={() => handleNavigation(item)}
                      isActive={isActive(item)}
                      data-testid={`sidebar-${item.id}`}
                    >
                      <Icon className="h-4 w-4" />
                      <span>{item.label}</span>
                    </SidebarMenuButton>
                  </SidebarMenuItem>
                );
              })}
            </SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>
      </SidebarContent>
    </Sidebar>
  );
}

client/src/components/FloatingAIBot.tsx.content:
import { useState, useRef, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Bot, Send, X, Minimize2, Maximize2, Sparkles, TrendingUp } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface AIResponse {
  success: boolean;
  analysis?: string;
  optimization?: {
    currentPercentage: number;
    targetPercentage: number;
    suggestions: string[];
    priority: 'high' | 'medium' | 'low';
  };
  timestamp: string;
}

export function FloatingAIBot() {
  const [isOpen, setIsOpen] = useState(false);
  const [isMinimized, setIsMinimized] = useState(false);
  const [query, setQuery] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [response, setResponse] = useState<string>("");
  const [optimization, setOptimization] = useState<any>(null);
  const { toast } = useToast();
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Auto-scroll to bottom quando ci sono nuovi messaggi
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [response, optimization, isLoading]);

  const handleAnalyze = async () => {
    if (!query.trim()) {
      toast({
        title: "Domanda richiesta",
        description: "Inserisci una domanda per l'assistente IA",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);
    try {
      const result: any = await apiRequest("POST", "/api/ai/analyze", { query });
      const response = await result.json();

      if (response.success && response.analysis) {
        setResponse(response.analysis);
        setOptimization(null); // Reset optimization quando si fa una domanda generale
      }
    } catch (error) {
      toast({
        title: "Errore",
        description: "Errore nell'analisi IA. Riprova più tardi.",
        variant: "destructive",
      });
      console.error("Errore analisi IA:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleFoodCostOptimization = async () => {
    setIsLoading(true);
    try {
      const result: any = await apiRequest("POST", "/api/ai/food-cost-optimization", {});
      const response = await result.json();

      if (response.success && response.optimization) {
        setOptimization(response.optimization);
        setResponse(""); // Reset response quando si fa ottimizzazione
        toast({
          title: "Ottimizzazione completata",
          description: "Analisi food cost generata con successo",
        });
      }
    } catch (error) {
      toast({
        title: "Errore",
        description: "Errore nell'ottimizzazione food cost. Riprova più tardi.",
        variant: "destructive",
      });
      console.error("Errore ottimizzazione:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high': return 'bg-red-100 text-red-800 border-red-200';
      case 'medium': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'low': return 'bg-green-100 text-green-800 border-green-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  // Pulsante flottante per aprire il bot
  if (!isOpen) {
    return (
      <div className="fixed bottom-6 right-6 z-50">
        <Button
          onClick={() => setIsOpen(true)}
          size="icon"
          className="h-14 w-14 rounded-full bg-primary hover:bg-primary/90 shadow-lg hover:shadow-xl transition-all duration-300 animate-pulse"
          data-testid="button-open-ai-bot"
        >
          <Bot className="h-6 w-6" />
        </Button>
      </div>
    );
  }

  // Chat bot popup
  return (
    <div className="fixed bottom-6 right-6 z-50">
      <Card 
        className={`w-[22rem] md:w-[28rem] lg:w-[32rem] max-w-[92vw] shadow-2xl border-primary/20 transition-all duration-300 flex flex-col ${
          isMinimized ? 'h-14' : 'h-[28rem] md:h-[34rem] max-h-[80vh]'
        }`}
      >
        <CardHeader className="pb-3 shrink-0">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <Bot className="h-5 w-5 text-primary" />
              <CardTitle className="text-sm">Assistente IA</CardTitle>
            </div>
            <div className="flex gap-1">
              <Button
                variant="ghost"
                size="icon"
                className="h-6 w-6"
                onClick={() => setIsMinimized(!isMinimized)}
                data-testid="button-minimize-ai-bot"
              >
                {isMinimized ? <Maximize2 className="h-3 w-3" /> : <Minimize2 className="h-3 w-3" />}
              </Button>
              <Button
                variant="ghost"
                size="icon"
                className="h-6 w-6"
                onClick={() => setIsOpen(false)}
                data-testid="button-close-ai-bot"
              >
                <X className="h-3 w-3" />
              </Button>
            </div>
          </div>
          {!isMinimized && (
            <CardDescription className="text-xs">
              Fai domande sui tuoi dati del ristorante
            </CardDescription>
          )}
        </CardHeader>

        {!isMinimized && (
          <CardContent className="flex-1 flex flex-col min-h-0">
            {/* Bottoni rapidi */}
            <div className="flex gap-1 mb-3">
              <Button
                variant="outline"
                size="sm"
                className="text-xs"
                onClick={handleFoodCostOptimization}
                disabled={isLoading}
                data-testid="button-quick-foodcost"
              >
                <TrendingUp className="h-3 w-3 mr-1" />
                Food Cost
              </Button>
              <Button
                variant="outline"
                size="sm"
                className="text-xs"
                onClick={() => {
                  setQuery("Quali sono i miei piatti più redditizi?");
                  handleAnalyze();
                }}
                disabled={isLoading}
                data-testid="button-quick-profitable"
              >
                <Sparkles className="h-3 w-3 mr-1" />
                Redditività
              </Button>
            </div>

            {/* Area messaggi */}
            <div className="flex-1 overflow-y-auto overscroll-contain scroll-smooth mb-3 text-xs min-h-0" style={{ WebkitOverflowScrolling: 'touch' }}>
              {response && (
                <div className="bg-muted/50 p-2 rounded text-xs mb-2">
                  <div className="whitespace-pre-wrap">{response}</div>
                </div>
              )}

              {isLoading && (
                <div className="bg-muted/30 p-2 rounded text-xs mb-2 flex items-center gap-2">
                  <div className="flex gap-1">
                    <div className="w-1 h-1 bg-primary rounded-full animate-bounce" style={{ animationDelay: '0ms' }}></div>
                    <div className="w-1 h-1 bg-primary rounded-full animate-bounce" style={{ animationDelay: '150ms' }}></div>
                    <div className="w-1 h-1 bg-primary rounded-full animate-bounce" style={{ animationDelay: '300ms' }}></div>
                  </div>
                  <span className="text-muted-foreground">L'IA sta analizzando...</span>
                </div>
              )}

              {optimization && (
                <div className="space-y-2">
                  <div className="grid grid-cols-2 gap-2">
                    <div className="bg-card p-2 rounded text-center">
                      <div className="text-lg font-bold text-red-600">
                        {optimization.currentPercentage}%
                      </div>
                      <div className="text-xs text-muted-foreground">Attuale</div>
                    </div>
                    <div className="bg-card p-2 rounded text-center">
                      <div className="text-lg font-bold text-green-600">
                        {optimization.targetPercentage}%
                      </div>
                      <div className="text-xs text-muted-foreground">Obiettivo</div>
                    </div>
                  </div>

                  <Badge className={`${getPriorityColor(optimization.priority)} text-xs`}>
                    Priorità {optimization.priority}
                  </Badge>

                  <div className="text-xs">
                    {optimization.suggestions.slice(0, 2).map((suggestion: string, index: number) => (
                      <div key={index} className="flex items-start gap-1 mb-1">
                        <span className="text-primary">•</span>
                        <span>{suggestion}</span>
                      </div>
                    ))}
                  </div>
                </div>
              )}
              <div ref={messagesEndRef} />
            </div>

            {/* Input domanda */}
            <div className="flex gap-2 relative z-10">
              <input
                type="text"
                placeholder="Fai una domanda..."
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleAnalyze()}
                className="flex-1 px-3 py-2 text-xs h-8 bg-white text-black border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                data-testid="input-ai-bot-query"
              />
              <Button 
                onClick={handleAnalyze} 
                disabled={isLoading || !query.trim()}
                size="icon"
                className="h-8 w-8"
                data-testid="button-send-ai-bot"
              >
                {isLoading ? (
                  <div className="w-3 h-3 border border-white border-t-transparent rounded-full animate-spin"></div>
                ) : (
                  <Send className="h-3 w-3" />
                )}
              </Button>
            </div>
          </CardContent>
        )}
      </Card>
    </div>
  );
}

server/gemini.ts.content:
import { GoogleGenAI } from "@google/genai";

// DON'T DELETE THIS COMMENT
// Follow these instructions when using this blueprint:
// - Note that the newest Gemini model series is "gemini-2.5-flash" or gemini-2.5-pro"
//   - do not change this unless explicitly requested by the user

// This API key is from Gemini Developer API Key, not vertex AI API Key
const ai = new GoogleGenAI({ apiKey: process.env.GEMINI_API_KEY || "" });

export async function analyzeRestaurantData(data: any, query: string): Promise<string> {
    const prompt = `Sei un esperto consulente di food cost per ristoranti. 
Analizza i seguenti dati del ristorante e rispondi alla domanda in modo pratico e specifico.

Dati del ristorante:
${JSON.stringify(data, null, 2)}

Domanda: ${query}

Fornisci una risposta dettagliata e consigli pratici per migliorare la gestione del ristorante, concentrandoti su food cost, margini e ottimizzazione operativa. Rispondi sempre in italiano.`;

    const response = await ai.models.generateContent({
        model: "gemini-2.5-flash",
        contents: prompt,
    });

    return response.text || "Mi dispiace, non riesco a elaborare la richiesta al momento.";
}

export interface FoodCostAnalysis {
    currentPercentage: number;
    targetPercentage: number;
    suggestions: string[];
    priority: 'high' | 'medium' | 'low';
}

export async function analyzeFoodCostOptimization(foodCostData: any): Promise<FoodCostAnalysis> {
    try {
        const systemPrompt = `Sei un esperto di food cost management per ristoranti.
Analizza i dati di food cost e fornisci suggerimenti per l'ottimizzazione.
Rispondi con JSON nel formato esatto:
{
  "currentPercentage": numero,
  "targetPercentage": numero, 
  "suggestions": ["suggerimento1", "suggerimento2", "suggerimento3"],
  "priority": "high" | "medium" | "low"
}`;

        const response = await ai.models.generateContent({
            model: "gemini-2.5-pro",
            config: {
                systemInstruction: systemPrompt,
                responseMimeType: "application/json",
                responseSchema: {
                    type: "object",
                    properties: {
                        currentPercentage: { type: "number" },
                        targetPercentage: { type: "number" },
                        suggestions: { 
                            type: "array",
                            items: { type: "string" }
                        },
                        priority: { 
                            type: "string",
                            enum: ["high", "medium", "low"]
                        },
                    },
                    required: ["currentPercentage", "targetPercentage", "suggestions", "priority"],
                },
            },
            contents: `Analizza questi dati di food cost: ${JSON.stringify(foodCostData)}`,
        });

        const rawJson = response.text;

        if (rawJson) {
            const data: FoodCostAnalysis = JSON.parse(rawJson);
            return data;
        } else {
            throw new Error("Empty response from model");
        }
    } catch (error) {
        console.error("Errore analisi food cost:", error);
        return {
            currentPercentage: 0,
            targetPercentage: 30,
            suggestions: ["Verifica i prezzi dei fornitori", "Monitora gli sprechi", "Ottimizza le porzioni"],
            priority: 'medium'
        };
    }
}

export async function generateMenuSuggestions(dishData: any, marketTrends: string = ""): Promise<string> {
    const prompt = `Sei un esperto chef e consulente di menu engineering per ristoranti.

Basandoti sui seguenti dati dei piatti:
${JSON.stringify(dishData, null, 2)}

E considerando questi trend di mercato: ${marketTrends}

Genera suggerimenti per:
1. Ottimizzazione del menu esistente
2. Nuovi piatti da introdurre
3. Strategie di pricing
4. Combinazioni ingredienti per ridurre i costi

Fornisci una risposta dettagliata e pratica in italiano.`;

    const response = await ai.models.generateContent({
        model: "gemini-2.5-flash",
        contents: prompt,
    });

    return response.text || "Non riesco a generare suggerimenti al momento.";
}

client/src/pages/Sales.tsx.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { TrendingUp, ShoppingCart, Plus, Save, Edit, Trash2, Euro, Package, Rocket, Calendar, Filter } from "lucide-react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import {
  useSales,
  useDishes,
  useCreateSale,
  useUpdateSale,
  useDeleteSale,
} from "@/hooks/useApi";
import type { Sales, Dish, InsertSales } from "@shared/schema";
import { insertSalesSchema } from "@shared/schema";
import { useToast } from "@/hooks/use-toast";

export default function Sales() {
  const [quantities, setQuantities] = useState<Record<string, number>>({});
  const [savingDishId, setSavingDishId] = useState<string | null>(null);
  const [editingSale, setEditingSale] = useState<Sales | undefined>();
  const [showForm, setShowForm] = useState(false);
  const [selectedDate, setSelectedDate] = useState<string>(new Date().toISOString().split('T')[0]);
  const [dateFromFilter, setDateFromFilter] = useState<string>('');
  const [dateToFilter, setDateToFilter] = useState<string>('');
  const { toast } = useToast();

  // Data fetching
  const { data: sales = [], isLoading: salesLoading } = useSales();
  const { data: dishes = [], isLoading: dishesLoading, isError: dishesError } = useDishes();

  // Mutations
  const createSaleMutation = useCreateSale();
  const updateSaleMutation = useUpdateSale();
  const deleteSaleMutation = useDeleteSale();

  // Form setup
  const form = useForm<InsertSales>({
    resolver: zodResolver(insertSalesSchema),
    defaultValues: {
      dishId: "",
      dishName: "",
      quantitySold: 1,
      unitCost: 0,
      unitRevenue: 0,
      saleDate: selectedDate,
      notes: "",
    },
  });

  // Filter sales based on date range filter
  const filteredSales = sales.filter(sale => {
    // If no filters are set, show all sales
    if (!dateFromFilter && !dateToFilter) return true;

    const saleDate = new Date(sale.saleDate);
    const fromDate = dateFromFilter ? new Date(dateFromFilter) : null;
    const toDate = dateToFilter ? new Date(dateToFilter) : null;

    // If only "from" date is set
    if (fromDate && !toDate) {
      return saleDate >= fromDate;
    }

    // If only "to" date is set
    if (!fromDate && toDate) {
      return saleDate <= toDate;
    }

    // If both dates are set
    if (fromDate && toDate) {
      return saleDate >= fromDate && saleDate <= toDate;
    }

    return true;
  });

  // Smart quantity handlers
  const handleQuantityChange = (dishId: string, quantity: number) => {
    setQuantities(prev => ({
      ...prev,
      [dishId]: quantity
    }));
  };

  const handleQuickSave = (dish: Dish, quantity: number) => {
    if (quantity <= 0) {
      toast({
        title: "Errore",
        description: "La quantità deve essere maggiore di 0",
        variant: "destructive"
      });
      return;
    }

    setSavingDishId(dish.id);

    const saleData: InsertSales = {
      dishId: dish.id,
      dishName: dish.name,
      quantitySold: quantity,
      unitCost: dish.totalCost,
      unitRevenue: dish.netPrice,
      saleDate: selectedDate,
      notes: `Vendita rapida del ${new Date(selectedDate).toLocaleDateString()}`
    };

    createSaleMutation.mutate(saleData, {
      onSuccess: () => {
        setQuantities(prev => ({ ...prev, [dish.id]: 0 }));
        setSavingDishId(null);
        toast({
          title: "Vendita registrata",
          description: `${quantity} ${dish.name} venduti con successo`
        });
      },
      onError: () => {
        setSavingDishId(null);
        toast({
          title: "Errore",
          description: "Errore nel salvataggio della vendita",
          variant: "destructive"
        });
      }
    });
  };

  // Original form handlers for advanced editing
  const handleCreateSale = (data: InsertSales) => {
    createSaleMutation.mutate(data);
    setShowForm(false);
    form.reset();
  };

  const handleUpdateSale = (data: InsertSales) => {
    if (editingSale) {
      updateSaleMutation.mutate({ 
        id: editingSale.id, 
        data: {
          ...data,
          notes: data.notes || undefined
        }
      });
      setEditingSale(undefined);
      setShowForm(false);
      form.reset();
    }
  };

  const handleDeleteSale = (id: string) => {
    deleteSaleMutation.mutate(id);
  };

  const handleEdit = (sale: Sales) => {
    setEditingSale(sale);
    setShowForm(true);
    form.reset({
      dishId: sale.dishId,
      dishName: sale.dishName,
      quantitySold: sale.quantitySold,
      unitCost: sale.unitCost,
      unitRevenue: sale.unitRevenue,
      saleDate: sale.saleDate,
      notes: sale.notes || "",
    });
  };

  const handleDishSelect = (dishId: string) => {
    const selectedDish = dishes.find(d => d.id === dishId);
    if (selectedDish) {
      form.setValue("dishName", selectedDish.name);
      form.setValue("unitCost", selectedDish.totalCost);
      form.setValue("unitRevenue", selectedDish.netPrice);
    }
  };

  const handleCancel = () => {
    setShowForm(false);
    setEditingSale(undefined);
    form.reset();
  };

  // Calculate totals based on filtered or all sales
  const salesForCalculation = (dateFromFilter || dateToFilter) ? filteredSales : sales;
  const totalQuantitySold = salesForCalculation.reduce((sum, sale) => sum + sale.quantitySold, 0);
  const totalCostOfSales = salesForCalculation.reduce((sum, sale) => sum + sale.totalCost, 0);
  const totalRevenue = salesForCalculation.reduce((sum, sale) => sum + sale.totalRevenue, 0);
  const totalProfit = totalRevenue - totalCostOfSales;

  return (
    <div className="container mx-auto p-6 space-y-6">
      {/* Header and Summary */}
      <div>
        <h1 className="text-3xl font-bold flex items-center gap-2">
          <ShoppingCart className="h-8 w-8 text-primary" />
          Vendite
        </h1>
        <p className="text-muted-foreground">
          Gestisci le vendite dei piatti e monitora le performance
        </p>
      </div>

      {/* Date Filters Section */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Filter className="h-5 w-5 text-primary" />
            Filtri Data
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="selectedDate" className="flex items-center gap-2">
                <Calendar className="h-4 w-4" />
                Data di Riferimento per Inserimento
              </Label>
              <Input
                id="selectedDate"
                type="date"
                value={selectedDate}
                onChange={(e) => {
                  setSelectedDate(e.target.value);
                  form.setValue("saleDate", e.target.value);
                }}
                data-testid="input-selected-date"
                className="w-full"
              />
              <p className="text-xs text-muted-foreground">
                Data che verrà utilizzata per le nuove vendite inserite rapidamente
              </p>
            </div>

            <div className="space-y-2">
              <Label className="flex items-center gap-2">
                <Filter className="h-4 w-4" />
                Filtra Vendite per Periodo
              </Label>
              <div className="grid grid-cols-2 gap-2">
                <div>
                  <Label htmlFor="dateFromFilter" className="text-xs text-muted-foreground">
                    DA
                  </Label>
                  <Input
                    id="dateFromFilter"
                    type="date"
                    value={dateFromFilter}
                    onChange={(e) => setDateFromFilter(e.target.value)}
                    data-testid="input-date-from-filter"
                    className="w-full"
                  />
                </div>
                <div>
                  <Label htmlFor="dateToFilter" className="text-xs text-muted-foreground">
                    A
                  </Label>
                  <Input
                    id="dateToFilter"
                    type="date"
                    value={dateToFilter}
                    onChange={(e) => setDateToFilter(e.target.value)}
                    data-testid="input-date-to-filter"
                    className="w-full"
                  />
                </div>
              </div>
              {(dateFromFilter || dateToFilter) && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => {
                    setDateFromFilter('');
                    setDateToFilter('');
                  }}
                  data-testid="button-clear-date-filters"
                  className="w-full"
                >
                  Mostra Tutto
                </Button>
              )}
              <p className="text-xs text-muted-foreground">
                Imposta DA e/o A per filtrare le vendite per periodo
              </p>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Smart Quick Sales Grid */}
      {dishesLoading && (
        <Card>
          <CardContent className="text-center py-12">
            <div className="animate-spin h-8 w-8 border-2 border-primary border-t-transparent rounded-full mx-auto mb-4"></div>
            <p className="text-muted-foreground">Caricamento piatti...</p>
          </CardContent>
        </Card>
      )}

      {dishesError && (
        <Card>
          <CardContent className="text-center py-12">
            <Package className="h-12 w-12 mx-auto text-destructive mb-4" />
            <h3 className="text-lg font-semibold mb-2">Errore nel caricamento</h3>
            <p className="text-muted-foreground mb-4">
              Non è stato possibile caricare i piatti. Riprova più tardi.
            </p>
          </CardContent>
        </Card>
      )}

      {!dishesLoading && !dishesError && dishes.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Rocket className="h-5 w-5" />
              Inserimento Rapido Vendite
            </CardTitle>
            <p className="text-sm text-muted-foreground">
              Inserisci la quantità venduta per ogni piatto
            </p>
          </CardHeader>
          <CardContent>
            <div className="space-y-2">
              {dishes.map((dish) => {
                const profit = dish.netPrice - dish.totalCost;
                const margin = dish.netPrice > 0 ? (profit / dish.netPrice * 100) : 0;

                return (
                  <div key={dish.id} className="flex items-center gap-3 p-2 rounded-md bg-muted/30 hover-elevate text-sm">
                    {/* Nome Piatto */}
                    <div className="flex-1 font-medium truncate min-w-0">
                      {dish.name}
                    </div>

                    {/* Costo */}
                    <div className="w-16 text-right font-mono text-muted-foreground">
                      €{dish.totalCost.toFixed(1)}
                    </div>

                    {/* Ricavo */}
                    <div className="w-16 text-right font-mono">
                      €{dish.netPrice.toFixed(1)}
                    </div>

                    {/* Profitto */}
                    <div className={`w-16 text-right font-mono ${
                      profit >= 0 ? 'text-green-600 dark:text-green-400' : 'text-destructive'
                    }`}>
                      €{profit.toFixed(1)}
                    </div>

                    {/* Margine */}
                    <div className="w-12 text-right font-mono text-xs">
                      {margin.toFixed(0)}%
                    </div>

                    {/* Campo Quantità Editabile */}
                    <div className="flex items-center gap-2">
                      <Input
                        type="number"
                        min="0"
                        max="999"
                        value={quantities[dish.id] || 0}
                        onChange={(e) => handleQuantityChange(dish.id, parseInt(e.target.value) || 0)}
                        className="w-16 h-8 text-center"
                        data-testid={`input-quantity-${dish.id}`}
                        placeholder="0"
                        disabled={savingDishId === dish.id}
                        onBlur={() => {
                          const qty = quantities[dish.id];
                          if (qty && qty > 0) {
                            handleQuickSave(dish, qty);
                          }
                        }}
                      />
                      {savingDishId === dish.id && (
                        <div className="animate-spin h-4 w-4 border-2 border-primary border-t-transparent rounded-full"></div>
                      )}
                    </div>
                  </div>
                );
              })}
            </div>
          </CardContent>
        </Card>
      )}

      {!dishesLoading && !dishesError && dishes.length === 0 && (
        <Card>
          <CardContent className="text-center py-12">
            <Package className="h-12 w-12 mx-auto text-muted-foreground mb-4" />
            <h3 className="text-lg font-semibold mb-2">Nessun piatto disponibile</h3>
            <p className="text-muted-foreground mb-4">
              Crea prima dei piatti nella sezione "Ricette" per poter registrare delle vendite.
            </p>
          </CardContent>
        </Card>
      )}

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <Package className="h-4 w-4 text-primary" />
              <div>
                <p className="text-sm font-medium text-muted-foreground">
                  Unità Vendute
                </p>
                <p className="text-2xl font-bold font-mono">{totalQuantitySold}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <Euro className="h-4 w-4 text-destructive" />
              <div>
                <p className="text-sm font-medium text-muted-foreground">
                  Costo Totale
                </p>
                <p className="text-2xl font-bold font-mono">€{totalCostOfSales.toFixed(1)}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <TrendingUp className="h-4 w-4 text-green-600" />
              <div>
                <p className="text-sm font-medium text-muted-foreground">
                  Ricavo Totale
                </p>
                <p className="text-2xl font-bold font-mono">€{totalRevenue.toFixed(1)}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-2">
              <TrendingUp className="h-4 w-4 text-chart-2" />
              <div>
                <p className="text-sm font-medium text-muted-foreground">
                  Profitto Totale
                </p>
                <p className={`text-2xl font-bold font-mono ${totalProfit >= 0 ? 'text-green-600' : 'text-destructive'}`}>
                  €{totalProfit.toFixed(1)}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Form */}
      {showForm && (
        <Card>
          <CardHeader>
            <CardTitle>
              {editingSale ? "Modifica Vendita" : "Nuova Vendita"}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form
                onSubmit={form.handleSubmit(editingSale ? handleUpdateSale : handleCreateSale)}
                className="space-y-4"
              >
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="dishId"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Piatto</FormLabel>
                        <FormControl>
                          <Select 
                            value={field.value} 
                            onValueChange={(value) => {
                              field.onChange(value);
                              handleDishSelect(value);
                            }}
                            data-testid="select-dish"
                          >
                            <SelectTrigger>
                              <SelectValue placeholder="Seleziona piatto" />
                            </SelectTrigger>
                            <SelectContent>
                              {dishes.map((dish) => (
                                <SelectItem key={dish.id} value={dish.id}>
                                  {dish.name}
                                </SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="dishName"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Nome Piatto</FormLabel>
                        <FormControl>
                          <Input {...field} data-testid="input-dish-name" readOnly />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="quantitySold"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Quantità Venduta</FormLabel>
                        <FormControl>
                          <Input
                            type="number"
                            min="1"
                            {...field}
                            onChange={(e) => field.onChange(parseInt(e.target.value) || 1)}
                            data-testid="input-quantity-sold"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="saleDate"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Data Vendita</FormLabel>
                        <FormControl>
                          <Input type="date" {...field} data-testid="input-sale-date" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="unitCost"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Costo Unitario (€)</FormLabel>
                        <FormControl>
                          <Input
                            type="number"
                            step="0.01"
                            min="0"
                            {...field}
                            onChange={(e) => field.onChange(parseFloat(e.target.value) || 0)}
                            data-testid="input-unit-cost"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="unitRevenue"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Ricavo Unitario (€)</FormLabel>
                        <FormControl>
                          <Input
                            type="number"
                            step="0.01"
                            min="0"
                            {...field}
                            onChange={(e) => field.onChange(parseFloat(e.target.value) || 0)}
                            data-testid="input-unit-revenue"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <FormField
                  control={form.control}
                  name="notes"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Note (opzionale)</FormLabel>
                      <FormControl>
                        <Textarea 
                          {...field}
                          value={field.value ?? ""}
                          data-testid="textarea-notes" 
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="flex gap-2">
                  <Button
                    type="submit"
                    disabled={createSaleMutation.isPending || updateSaleMutation.isPending}
                    data-testid="button-submit-sale"
                  >
                    {editingSale ? "Aggiorna" : "Crea"} Vendita
                  </Button>
                  <Button
                    type="button"
                    variant="outline"
                    onClick={handleCancel}
                    data-testid="button-cancel-sale"
                  >
                    Annulla
                  </Button>
                </div>
              </form>
            </Form>
          </CardContent>
        </Card>
      )}

      {/* Sales List */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <TrendingUp className="h-5 w-5 text-primary" />
            Vendite Registrate
          </CardTitle>
        </CardHeader>
        <CardContent>
          {salesLoading ? (
            <p className="text-center text-muted-foreground">Caricamento vendite...</p>
          ) : (
            <>
              {(dateFromFilter || dateToFilter) && (
                <div className="mb-4 p-3 bg-muted rounded-lg">
                  <p className="text-sm text-muted-foreground flex items-center gap-2">
                    <Filter className="h-4 w-4" />
                    {dateFromFilter && dateToFilter && (
                      <>Mostrando vendite dal {new Date(dateFromFilter).toLocaleDateString()} al {new Date(dateToFilter).toLocaleDateString()}</>
                    )}
                    {dateFromFilter && !dateToFilter && (
                      <>Mostrando vendite dal {new Date(dateFromFilter).toLocaleDateString()} in poi</>
                    )}
                    {!dateFromFilter && dateToFilter && (
                      <>Mostrando vendite fino al {new Date(dateToFilter).toLocaleDateString()}</>
                    )}
                  </p>
                  <Badge variant="secondary" className="mt-2">
                    {filteredSales.length} di {sales.length} vendite
                  </Badge>
                </div>
              )}
              {!dateFromFilter && !dateToFilter && sales.length > 0 && (
                <div className="mb-4 p-3 bg-muted rounded-lg">
                  <Badge variant="secondary">
                    Totale: {sales.length} vendite
                  </Badge>
                </div>
              )}
              {((dateFromFilter || dateToFilter) ? filteredSales : sales).length === 0 ? (
                <p className="text-center text-muted-foreground italic">
                  {(dateFromFilter || dateToFilter) ? `Nessuna vendita registrata per il periodo selezionato` : "Nessuna vendita ancora registrata."}
                </p>
              ) : (
                <div className="space-y-3">
                  {((dateFromFilter || dateToFilter) ? filteredSales : sales).map((sale) => (
                <div
                  key={sale.id}
                  className="flex justify-between items-center p-4 bg-muted rounded-lg hover-elevate"
                >
                  <div className="flex-1">
                    <div className="flex items-center gap-2 mb-2">
                      <span className="font-semibold">{sale.dishName}</span>
                      <Badge variant="outline">x{sale.quantitySold}</Badge>
                      <Badge variant="secondary" className="text-xs">
                        {sale.saleDate}
                      </Badge>
                    </div>
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                      <div>
                        <span className="text-muted-foreground">Costo Tot:</span>{" "}
                        <span className="font-mono">€{sale.totalCost.toFixed(1)}</span>
                      </div>
                      <div>
                        <span className="text-muted-foreground">Ricavo Tot:</span>{" "}
                        <span className="font-mono">€{sale.totalRevenue.toFixed(1)}</span>
                      </div>
                      <div>
                        <span className="text-muted-foreground">Profitto:</span>{" "}
                        <span className={`font-mono ${(sale.totalRevenue - sale.totalCost) >= 0 ? 'text-green-600' : 'text-destructive'}`}>
                          €{(sale.totalRevenue - sale.totalCost).toFixed(1)}
                        </span>
                      </div>
                      <div>
                        <span className="text-muted-foreground">Margine:</span>{" "}
                        <span className="font-mono">
                          {sale.totalRevenue > 0 ? ((sale.totalRevenue - sale.totalCost) / sale.totalRevenue * 100).toFixed(1) : '0.0'}%
                        </span>
                      </div>
                    </div>
                    {sale.notes && (
                      <p className="text-sm text-muted-foreground mt-2">
                        {sale.notes}
                      </p>
                    )}
                  </div>

                  <div className="flex gap-2">
                    <Button
                      variant="outline"
                      size="icon"
                      onClick={() => handleEdit(sale)}
                      data-testid={`button-edit-sale-${sale.id}`}
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="destructive"
                      size="icon"
                      onClick={() => handleDeleteSale(sale.id)}
                      data-testid={`button-delete-sale-${sale.id}`}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              ))}
                </div>
              )}
            </>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/lib/priceCalculations.ts.content:
// Utility functions for price calculations with real food cost considering waste and weight adjustments

export interface Product {
  id: string;
  name: string;
  unit: string;
  pricePerUnit: number;
  wastePercentage: number;
}

export interface Recipe {
  id: string;
  name: string;
  ingredients: Array<{
    productId: string;
    quantity: number;
    cost: number;
  }>;
  weightAdjustment: number;
  totalCost: number;
  sellingPrice?: number;
}

export interface RecipeIngredient {
  productId: string;
  quantity: number;
  cost: number;
}

export interface DishIngredient {
  productId?: string;
  recipeId?: string;
  quantity: number;
  cost: number;
}

// Default target food cost percentages
export const DEFAULT_TARGET_FOOD_COST_PERCENTAGE = 30; // 30%

/**
 * Calculate the real food cost for a recipe considering weight adjustment
 * @param totalCost - Total raw ingredient cost
 * @param weightAdjustment - Weight change percentage (e.g., -50 for 50% loss)
 * @returns Real food cost per final kg/unit
 */
export function calculateRecipeRealFoodCost(totalCost: number, weightAdjustment: number): number {
  if (weightAdjustment === -100) {
    throw new Error("Weight adjustment cannot be -100% (complete loss)");
  }

  // Weight adjustment affects final yield
  // -50% means we get 50% less product, so cost per kg doubles
  // +70% means we get 70% more product, so cost per kg decreases
  const multiplier = 1 + (weightAdjustment / 100);

  // Real cost per final unit = raw cost / final yield multiplier
  return totalCost / multiplier;
}

/**
 * Calculate the real food cost for a product considering waste
 * @param pricePerUnit - Gross price per unit
 * @param wastePercentage - Waste percentage (e.g., 5 for 5% waste)
 * @returns Real food cost per usable unit
 */
export function calculateProductRealFoodCost(pricePerUnit: number, wastePercentage: number): number {
  // Waste affects usable quantity
  // 5% waste means we only get 95% usable, so cost per usable unit increases
  const usablePercentage = (100 - wastePercentage) / 100;
  return pricePerUnit / usablePercentage;
}

/**
 * Calculate suggested selling price based on real food cost and target percentage
 * @param realFoodCost - Real food cost per unit considering waste/weight adjustments
 * @param targetFoodCostPercentage - Target food cost percentage (default: 30%)
 * @returns Suggested selling price
 */
export function calculateSuggestedPrice(
  realFoodCost: number, 
  targetFoodCostPercentage: number = DEFAULT_TARGET_FOOD_COST_PERCENTAGE
): number {
  if (targetFoodCostPercentage <= 0 || targetFoodCostPercentage >= 100) {
    throw new Error("Target food cost percentage must be between 0 and 100");
  }

  return realFoodCost / (targetFoodCostPercentage / 100);
}

/**
 * Calculate suggested price for a recipe considering weight adjustment
 * @param totalCost - Total raw ingredient cost
 * @param weightAdjustment - Weight adjustment percentage
 * @param targetFoodCostPercentage - Target food cost percentage (default: 30%)
 * @returns Suggested selling price per final kg/unit
 */
export function calculateRecipeSuggestedPrice(
  totalCost: number,
  weightAdjustment: number,
  targetFoodCostPercentage: number = DEFAULT_TARGET_FOOD_COST_PERCENTAGE
): number {
  const realFoodCost = calculateRecipeRealFoodCost(totalCost, weightAdjustment);
  return calculateSuggestedPrice(realFoodCost, targetFoodCostPercentage);
}

/**
 * Calculate the real food cost for a recipe ingredient considering both waste and weight adjustment
 * @param product - Product information
 * @param quantity - Quantity of product used
 * @param ingredientWeightAdjustment - Weight adjustment specific to this ingredient in the recipe
 * @returns Real food cost for this recipe ingredient
 */
export function calculateRecipeIngredientRealFoodCost(
  product: Product,
  quantity: number,
  ingredientWeightAdjustment: number = 0
): number {
  // Step 1: Calculate cost considering waste
  const costWithWaste = calculateProductRealFoodCost(product.pricePerUnit, product.wastePercentage) * quantity;

  // Step 2: Apply weight adjustment for this specific ingredient
  if (ingredientWeightAdjustment === -100) {
    throw new Error("Weight adjustment cannot be -100% (complete loss)");
  }

  const multiplier = 1 + (ingredientWeightAdjustment / 100);
  return costWithWaste / multiplier;
}

/**
 * Calculate the real total cost of a recipe considering both waste and weight adjustments
 * @param ingredients - Recipe ingredients with weight adjustments
 * @param products - Map of products by ID
 * @param recipeWeightAdjustment - Overall recipe weight adjustment
 * @returns Real total cost of the recipe
 */
export function calculateRecipeRealTotalCost(
  ingredients: Array<{productId: string, quantity: number, cost: number, weightAdjustment?: number}>,
  products: Map<string, Product>,
  recipeWeightAdjustment: number = 0
): number {
  let totalRealCost = 0;

  for (const ingredient of ingredients) {
    const product = products.get(ingredient.productId);
    if (product) {
      const ingredientRealCost = calculateRecipeIngredientRealFoodCost(
        product,
        ingredient.quantity,
        ingredient.weightAdjustment || 0
      );
      totalRealCost += ingredientRealCost;
    }
  }

  // Apply overall recipe weight adjustment
  if (recipeWeightAdjustment !== 0) {
    if (recipeWeightAdjustment === -100) {
      throw new Error("Recipe weight adjustment cannot be -100% (complete loss)");
    }
    const multiplier = 1 + (recipeWeightAdjustment / 100);
    totalRealCost = totalRealCost / multiplier;
  }

  return totalRealCost;
}

/**
 * Calculate suggested price for a dish considering ingredient waste and recipe weight adjustments
 * @param ingredients - Array of dish ingredients (can be products or recipes)
 * @param products - Map of products by ID
 * @param recipes - Map of recipes by ID
 * @param targetFoodCostPercentage - Target food cost percentage (default: 30%)
 * @returns Suggested selling price for the dish
 */
export function calculateDishSuggestedPrice(
  ingredients: DishIngredient[],
  products: Map<string, Product>,
  recipes: Map<string, Recipe>,
  targetFoodCostPercentage: number = DEFAULT_TARGET_FOOD_COST_PERCENTAGE
): number {
  let totalRealFoodCost = 0;

  for (const ingredient of ingredients) {
    if (ingredient.productId) {
      // Product ingredient - consider waste
      const product = products.get(ingredient.productId);
      if (product) {
        const realCostPerUnit = calculateProductRealFoodCost(product.pricePerUnit, product.wastePercentage);
        totalRealFoodCost += realCostPerUnit * ingredient.quantity;
      }
    } else if (ingredient.recipeId) {
      // Recipe ingredient - calculate real cost considering both ingredient weight adjustments and recipe weight adjustment
      const recipe = recipes.get(ingredient.recipeId);
      if (recipe) {
        const recipeRealCost = calculateRecipeRealTotalCost(
          recipe.ingredients,
          products,
          recipe.weightAdjustment
        );
        totalRealFoodCost += recipeRealCost * ingredient.quantity;
      }
    }
  }

  return calculateSuggestedPrice(totalRealFoodCost, targetFoodCostPercentage);
}

/**
 * Format price with currency symbol
 * @param price - Price to format
 * @returns Formatted price string
 */
export function formatPrice(price: number): string {
  return `€${price.toFixed(2)}`;
}

/**
 * Calculate actual food cost percentage
 * @param foodCost - Actual food cost
 * @param sellingPrice - Selling price
 * @returns Food cost percentage
 */
export function calculateFoodCostPercentage(foodCost: number, sellingPrice: number): number {
  if (sellingPrice <= 0) return 0;
  return (foodCost / sellingPrice) * 100;
}

